CREATE OR REPLACE PROCEDURE man_comunic_atividade ( nr_sequencia_p bigint, ds_perfil_adicional_p text, ds_setor_adicional_p text, nm_usuario_destino_p text, nr_seq_grupo_usuario_p text, nm_usuario_p text) AS $body$
DECLARE

			 
/* Campos C.I */
 
nr_seq_classif_w			bigint;
nm_usuario_grupo_w		varchar(15);
ds_usuarios_destino_w		varchar(4000);
ds_titulo_w			varchar(255);
ds_comunicado_w			varchar(2000);

/* Campos Atividade */
 
nr_seq_ordem_serv_w		bigint;
dt_atividade_w			timestamp;
ds_usuario_exec_w			varchar(60);
ds_funcao_w			varchar(80);
qt_minuto_w			bigint;
ds_observacao_w			varchar(4000);
ds_atividade_w			varchar(2000);
	
c01 CURSOR FOR 
	SELECT	distinct 
		nm_usuario_grupo 
	from	usuario_grupo 
	where	nr_seq_grupo	= nr_seq_grupo_usuario_p 
	and	ie_situacao	= 'A';


BEGIN 
ds_usuarios_destino_w := nm_usuario_destino_p;
 
if (coalesce(nr_seq_grupo_usuario_p,0) > 0) then 
	open C01;
	loop 
	fetch C01 into	 
		nm_usuario_grupo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		ds_usuarios_destino_w := substr(ds_usuarios_destino_w || nm_usuario_grupo_w || ', ',1,4000);
		end;
	end loop;
	close C01;
end if;
 
if (coalesce(nr_sequencia_p, 0) > 0) then 
	begin 
	select	nr_seq_ordem_serv, 
		dt_atividade, 
		substr(obter_pf_usuario(nm_usuario_exec,'N'),1,60), 
		substr(obter_desc_tipo_funcao(nr_seq_funcao),1,80), 
		qt_minuto, 
		ds_observacao, 
		ds_atividade 
	into STRICT	nr_seq_ordem_serv_w, 
		dt_atividade_w, 
		ds_usuario_exec_w, 
		ds_funcao_w, 
		qt_minuto_w, 
		ds_observacao_w, 
		ds_atividade_w 
	from	man_ordem_serv_ativ 
	where	nr_sequencia = nr_sequencia_p;
	 
	ds_titulo_w	:= substr(wheb_mensagem_pck.get_texto(305627,'NR_ORDEM_SERVICO=' || nr_seq_ordem_serv_w),1,255);
	ds_comunicado_w	:= substr( 
				substr(wheb_mensagem_pck.get_texto(305631),1,30) || ': ' || to_char(dt_atividade_w,'dd/mm/yyyy hh24:mi:ss') || chr(13) || chr(10) || 
				substr(wheb_mensagem_pck.get_texto(305619),1,30) || ': ' || ds_usuario_exec_w || chr(13) || chr(10) || 
				substr(wheb_mensagem_pck.get_texto(305620),1,30) || ': ' || ds_funcao_w || chr(13) || chr(10) || 
				substr(wheb_mensagem_pck.get_texto(305621),1,30) || ': ' || qt_minuto_w || chr(13) || chr(10) || chr(13) || chr(10) || 
				substr(wheb_mensagem_pck.get_texto(305622),1,30) || ': ' || ds_atividade_w || chr(13) || chr(10) || chr(13) || chr(10) || 
				substr(wheb_mensagem_pck.get_texto(305625),1,30) || ': ' || ds_observacao_w,1,2000);
				 
	if	((coalesce(ds_usuarios_destino_w, 'X') <> 'X') or (coalesce(ds_perfil_adicional_p, 'X') <> 'X') or (coalesce(ds_setor_adicional_p, 'X') <> 'X')) then 
		begin 
		select	obter_classif_comunic('F') 
		into STRICT	nr_seq_classif_w 
		;
 
		insert into comunic_interna( 
			dt_comunicado, 
			ds_titulo, 
			ds_comunicado, 
			nm_usuario, 
			dt_atualizacao, 
			ie_geral, 
			nm_usuario_destino, 
			nr_sequencia, 
			ie_gerencial, 
			nr_seq_classif, 
			dt_liberacao, 
			ds_perfil_adicional, 
			ds_setor_adicional) 
		values (	clock_timestamp(), 
			ds_titulo_w, 
			ds_comunicado_w, 
			nm_usuario_p, 
			clock_timestamp(), 
			'N', 
			ds_usuarios_destino_w, 
			nextval('comunic_interna_seq'), 
			'N', 
			nr_seq_classif_w, 
			clock_timestamp(), 
			ds_perfil_adicional_p, 
			ds_setor_adicional_p);
		commit;
		end;
	end if;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
