SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_comunic_hist_transf_anexo ( nr_seq_ordem_serv_p bigint, ds_comunicado_p text, ds_titulo_p text, ds_perfil_adicional_p text, ds_setor_adicional_p text, nm_usuario_destino_p text, ie_anexo_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ie_transf_anexo_p text) AS $body$
DECLARE



c01 CURSOR FOR
SELECT	ds_arquivo
from	man_ordem_serv_arq
where	ie_anexar_email	= 'S'
and	nr_seq_ordem 	= nr_seq_ordem_serv_p;

nr_sequencia_w	bigint;
ds_arquivo_w	varchar(255);
nr_seq_classif_w	bigint;
ds_caminho_w	varchar(255);


BEGIN

select	max(obter_valor_param_usuario(299, 183, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p))
into STRICT	ds_caminho_w
;


/* if	(nm_usuario_destino_p is not null) then   - Removido "If" para permitir enviar apenas para Setor ou Perfil" */

	begin
	select	obter_classif_comunic('F')
	into STRICT	nr_seq_classif_w
	;

	select	nextval('comunic_interna_seq')
	into STRICT	nr_sequencia_w
	;
	insert into comunic_interna(
		dt_comunicado,
		ds_titulo,
		ds_comunicado,
		nm_usuario,
		dt_atualizacao,
		ie_geral,
		nm_usuario_destino,
		nr_sequencia,
		ie_gerencial,
		nr_seq_classif,
		dt_liberacao,
		ds_perfil_adicional,
		ds_setor_adicional)
	values (	clock_timestamp(),
		ds_titulo_p,
		ds_comunicado_p,
		nm_usuario_p,
		clock_timestamp(),
		'N',
		nm_usuario_destino_p,
		nr_sequencia_w,
		'N',
		nr_seq_classif_w,
		clock_timestamp(),
		ds_perfil_adicional_p,
		ds_setor_adicional_p);

	if (ie_anexo_p = 'S') then
		begin
		open c01;
		loop
		fetch c01 into
			ds_arquivo_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin

			if (ie_transf_anexo_p = 'S') then
				while(position('\' in ds_arquivo_w) > 0) loop
					begin
					ds_arquivo_w := SUBSTR(ds_arquivo_w,position('\' in ds_arquivo_w)+1, length(ds_arquivo_w));
					end;
				end loop;
			ds_arquivo_w := ds_caminho_w||ds_arquivo_w;
			end if;

			insert into comunic_interna_arq(
						nr_sequencia,
						nr_seq_comunic,
						dt_atualizacao,
						nm_usuario,
						ds_arquivo)
					values (	nextval('comunic_interna_arq_seq'),
						nr_sequencia_w,
						clock_timestamp(),
						nm_usuario_p,
						ds_arquivo_w);
			end;
		end loop;
		close c01;
		end;

	insert into man_ordem_serv_envio(
		nr_sequencia,
		nr_seq_ordem,
		dt_atualizacao,
		nm_usuario,
		dt_envio,
		ie_tipo_envio,
		ds_destino,
		ds_observacao)
	values (	nextval('man_ordem_serv_envio_seq'),
		nr_seq_ordem_serv_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		'I',
		coalesce(substr(nm_usuario_destino_p,1,255),'Enviado apenas para Perfil ou Setor'),
		'Comunicação histórico');
	commit;
	end if;
	end;
--end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
