SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_consiste_exec_os ( nr_sequencia_p bigint, nr_seq_ordem_serv_p bigint, nm_usuario_exec_p text, dt_atividade_p timestamp, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_reg_w			bigint;
nr_seq_tipo_projeto_w	proj_projeto.nr_seq_tipo_projeto%type;
qt_registros_w		bigint;
nm_etapa_w		varchar(80);

c01 CURSOR FOR
	SELECT	c.nm_etapa
	from	proj_cron_etapa b,
		prp_etapa_processo c
	where	c.nr_sequencia = b.nr_seq_etapa_npi
	and	b.pr_etapa < 100
	and	exists (	SELECT	1
			from	man_ordem_servico a,
				prp_etapa_dependencia x,
				proj_cron_etapa y,
				prp_etapa_processo z
			where	x.nr_seq_etapa_processo = c.nr_sequencia
			and 	a.nr_seq_proj_cron_etapa = y.nr_sequencia
			and	y.nr_seq_etapa_npi = z.nr_sequencia
			and	x.nr_seq_etapa_principal = z.nr_sequencia
			and 	a.nr_sequencia = nr_seq_ordem_serv_p
			and 	x.nr_seq_tipo_projeto = nr_seq_tipo_projeto_w
			and 	b.nr_seq_cronograma = y.nr_seq_cronograma);


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	MAN_ORDEM_SERV_ATIV
where	nr_seq_ordem_serv	= nr_seq_ordem_serv_p
and	NM_USUARIO_EXEC = NM_USUARIO_EXEC_p
and	dt_atividade between trunc(dt_atividade_p) and fim_dia(trunc(dt_atividade_p))
and	nr_sequencia <> nr_sequencia_p
and	coalesce(dt_fim_atividade::text, '') = '';


if (qt_reg_w	> 0) then
	ds_retorno_p	:= 'Somente é possível executar uma atividade por vez nesta OS. ';
end if;

if (coalesce(ds_retorno_p::text, '') = '') then

	select	max(nr_seq_tipo_projeto)
	into STRICT	nr_seq_tipo_projeto_w
	from	man_ordem_servico a,
		proj_projeto b,
		proj_cronograma c,
		proj_cron_etapa d
	WHERE	d.nr_seq_cronograma = c.nr_sequencia
	AND	b.nr_sequencia = c.nr_seq_proj
	AND	a.nr_seq_proj_cron_etapa = d.nr_sequencia
	and	a.nr_sequencia = nr_seq_ordem_serv_p;

	if (nr_seq_tipo_projeto_w IS NOT NULL AND nr_seq_tipo_projeto_w::text <> '') then

		SELECT	count(*)
		into STRICT	qt_registros_w
		FROM	proj_cron_etapa b,
			prp_etapa_processo c
		WHERE	c.nr_sequencia = b.nr_seq_etapa_npi
		AND	b.PR_ETAPA < 100
		and	exists (	SELECT	1
				from	man_ordem_servico a,
					prp_etapa_dependencia x,
					proj_cron_etapa y,
					prp_etapa_processo z
				where	x.nr_seq_etapa_processo = c.nr_sequencia
				AND 	a.nr_seq_proj_cron_etapa = y.nr_Sequencia
				AND		y.nr_seq_etapa_npi = z.nr_sequencia
				AND		x.nr_Seq_etapa_principal = z.nr_sequencia
				AND 	a.nr_sequencia = nr_seq_ordem_serv_p
				AND 	x.nr_seq_tipo_projeto = nr_seq_tipo_projeto_w
				AND 	b.nr_seq_cronograma = y.nr_Seq_cronograma);

		if (qt_registros_w > 0) then
			if (qt_registros_w = 1) then
				ds_retorno_p := wheb_mensagem_pck.get_texto(314053);
			else
				ds_retorno_p := wheb_mensagem_pck.get_texto(314133);
			end if;
			open C01;
			loop
			fetch C01 into
				nm_etapa_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				ds_retorno_p := ds_retorno_p || chr(13) || '- '||nm_etapa_w;
			end loop;
			ds_retorno_p := ds_retorno_p || chr(13) || wheb_mensagem_pck.get_texto(314135);
			close c01;
		end if;
	end if;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_consiste_exec_os ( nr_sequencia_p bigint, nr_seq_ordem_serv_p bigint, nm_usuario_exec_p text, dt_atividade_p timestamp, ds_retorno_p INOUT text) FROM PUBLIC;

