SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_consiste_inicio_ativ_os ( nr_seq_ordem_serv_p bigint, ie_consiste_prior_p text, nm_usuario_p text, nr_seq_os_iniciada_p INOUT bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE


nr_seq_ordem_w		bigint;
ie_prioridade_desen_w	smallint;
nr_seq_tipo_projeto_w	proj_projeto.nr_seq_tipo_projeto%type;
qt_registros_w		bigint;
nm_etapa_w		varchar(80);

c01 CURSOR FOR
	SELECT	c.nm_etapa
	from	proj_cron_etapa b,
		prp_etapa_processo c
	where	c.nr_sequencia = b.nr_seq_etapa_npi
	and	b.pr_etapa < 100
	and	exists (	SELECT	1
			from	man_ordem_servico a,
				prp_etapa_dependencia x,
				proj_cron_etapa y,
				prp_etapa_processo z
			where	x.nr_seq_etapa_processo = c.nr_sequencia
			and 	a.nr_seq_proj_cron_etapa = y.nr_sequencia
			and	y.nr_seq_etapa_npi = z.nr_sequencia
			and	x.nr_seq_etapa_principal = z.nr_sequencia
			and 	a.nr_sequencia = nr_seq_ordem_serv_p
			and 	x.nr_seq_tipo_projeto = nr_seq_tipo_projeto_w
			and 	b.nr_seq_cronograma = y.nr_seq_cronograma);


BEGIN
if (nr_seq_ordem_serv_p IS NOT NULL AND nr_seq_ordem_serv_p::text <> '') and (nr_seq_ordem_serv_p <> 0) then
	select	max(a.ie_prioridade_desen)
	into STRICT	ie_prioridade_desen_w
	from	man_ordem_ativ_prev	a
	where	a.nr_seq_ordem_serv	= nr_seq_ordem_serv_p
	and	a.dt_prevista between trunc(clock_timestamp(),'dd') and fim_dia(clock_timestamp());

	select	max(a.nr_seq_ordem_serv)
	into STRICT	nr_seq_os_iniciada_p
	from	man_ordem_serv_ativ a
	where	nr_seq_ordem_serv	<> nr_seq_ordem_serv_p
	and	coalesce(dt_fim_atividade::text, '') = ''
	and	nm_usuario_exec		= nm_usuario_p
	and	dt_atividade between trunc(clock_timestamp(),'dd') and fim_dia(clock_timestamp());

	if (ie_consiste_prior_p in ('S','A')) then
		select	max(c.nr_seq_ordem_serv)
		into STRICT	nr_seq_ordem_w
		from	man_ordem_servico	a,
			man_estagio_processo	b,
			man_ordem_ativ_prev	c
		where	b.nr_sequencia 		= a.nr_seq_estagio
		and	a.nr_sequencia		= c.nr_seq_ordem_serv
		and (b.ie_desenv    	= 'S' or b.ie_tecnologia = 'S')
		and	c.ie_prioridade_desen > ie_prioridade_desen_w
		and	a.nr_sequencia		<> nr_seq_ordem_serv_p
		and	c.nm_usuario_prev	= nm_usuario_p
		and	c.dt_prevista between trunc(clock_timestamp(),'dd') and fim_dia(clock_timestamp())
		and	coalesce(c.dt_real::text, '') = '';

		if (nr_seq_ordem_w IS NOT NULL AND nr_seq_ordem_w::text <> '') then
			ds_mensagem_p	:= 'Existem atividades previstas com maior prioridade, verifique as demais atividades. Ordem: ' || nr_seq_ordem_w;
			nr_seq_os_iniciada_p	:= null;
		end if;
	end if;

	if (nr_seq_os_iniciada_p IS NOT NULL AND nr_seq_os_iniciada_p::text <> '') then
		ds_mensagem_p	:= ds_mensagem_p || wheb_mensagem_pck.get_texto(326207, 'NR_SEQ_ORDEM=' || nr_seq_os_iniciada_p); --A OS X está em execução no momento, deseja finalizar a atividade nela?
	end if;


	select	max(nr_seq_tipo_projeto)
	into STRICT	nr_seq_tipo_projeto_w
	from	man_ordem_servico a,
		proj_projeto b,
		proj_cronograma c,
		proj_cron_etapa d
	WHERE	d.nr_seq_cronograma = c.nr_sequencia
	AND	b.nr_sequencia = c.nr_seq_proj
	AND	a.nr_seq_proj_cron_etapa = d.nr_sequencia
	and	a.nr_sequencia = nr_seq_ordem_serv_p;

	if (nr_seq_tipo_projeto_w IS NOT NULL AND nr_seq_tipo_projeto_w::text <> '') then

		SELECT	count(*)
		into STRICT	qt_registros_w
		FROM	proj_cron_etapa b,
			prp_etapa_processo c
		WHERE	c.nr_sequencia = b.nr_seq_etapa_npi
		AND	b.PR_ETAPA < 100
		and	exists (	SELECT	1
				from	man_ordem_servico a,
					prp_etapa_dependencia x,
					proj_cron_etapa y,
					prp_etapa_processo z
				where	x.nr_seq_etapa_processo = c.nr_sequencia
				AND 	a.nr_seq_proj_cron_etapa = y.nr_Sequencia
				AND	y.nr_seq_etapa_npi = z.nr_sequencia
				AND	x.nr_Seq_etapa_principal = z.nr_sequencia
				AND 	a.nr_sequencia = nr_seq_ordem_serv_p
				AND 	x.nr_seq_tipo_projeto = nr_seq_tipo_projeto_w
				AND 	b.nr_seq_cronograma = y.nr_Seq_cronograma);

		if (qt_registros_w > 0) then
			if (qt_registros_w = 1) then
				ds_mensagem_p := wheb_mensagem_pck.get_texto(314053);
			else
				ds_mensagem_p := wheb_mensagem_pck.get_texto(314133);
			end if;
			open C01;
			loop
			fetch C01 into
				nm_etapa_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				ds_mensagem_p := ds_mensagem_p || chr(13) || '- ' || nm_etapa_w;
			end loop;
			ds_mensagem_p := ds_mensagem_p || chr(13) || wheb_mensagem_pck.get_texto(314135);
			close c01;
		end if;
	end if;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_consiste_inicio_ativ_os ( nr_seq_ordem_serv_p bigint, ie_consiste_prior_p text, nm_usuario_p text, nr_seq_os_iniciada_p INOUT bigint, ds_mensagem_p INOUT text) FROM PUBLIC;

