CREATE OR REPLACE PROCEDURE man_cons_fim_atividade_js ( nr_seq_ordem_p bigint, qt_projeto_p bigint, ds_atividade_p text, ds_observacao_p text, dt_fim_atividade_p timestamp, nm_usuario_p text, pr_realizacao_os_p bigint, ie_finalizar_ativ_prev_p text, ds_retorno_p INOUT text, ds_aviso_p INOUT text ) AS $body$
DECLARE


nr_seq_proj_cron_etapa_p	bigint;
dt_inicio_atividade_w		timestamp;
nr_seq_atividade_w		bigint;
nr_seq_proj_cron_etapa_w	bigint;


BEGIN

select	max(nr_sequencia),
	max(nr_seq_proj_cron_etapa)
into STRICT	nr_seq_atividade_w,
	nr_seq_proj_cron_etapa_w
from	man_ordem_serv_ativ
where	nr_seq_ordem_serv	= nr_seq_ordem_p
and	nm_usuario_exec		= nm_usuario_p;

if (nr_seq_atividade_w IS NOT NULL AND nr_seq_atividade_w::text <> '')
	and (nr_seq_proj_cron_etapa_w IS NOT NULL AND nr_seq_proj_cron_etapa_w::text <> '')
	and (qt_projeto_p < 0 or qt_projeto_p > 100) then
		ds_retorno_p := obter_texto_tasy(117217, wheb_usuario_pck.get_nr_seq_idioma);
		goto final;
end if;

SELECT * FROM man_finalizar_ativ_iniciada(nr_seq_ordem_p, ds_atividade_p, ds_observacao_p, dt_fim_atividade_p, nm_usuario_p, '', pr_realizacao_os_p, ie_finalizar_ativ_prev_p, ds_aviso_p, nr_seq_atividade_w, nr_seq_proj_cron_etapa_w) INTO STRICT ds_aviso_p, nr_seq_atividade_w, nr_seq_proj_cron_etapa_w;

CALL atualizar_realizacao_ativ_proj(nr_seq_proj_cron_etapa_w,qt_projeto_p,nm_usuario_p);

CALL man_ajustar_estagio_OS(nr_seq_ordem_p, null);

<<final>>
commit;

end;
$body$
