CREATE OR REPLACE PROCEDURE man_definir_data_exec_prevista ( nr_seq_ordem_serv_p bigint, dt_previsao_p timestamp, nm_usuario_prev_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_grupo_w			bigint;
nm_usuario_grupo_w		varchar(15);
dt_previsao_w			timestamp;

C01 CURSOR FOR
	SELECT	nm_usuario_grupo
	from	usuario_grupo_des
	where	nr_seq_grupo		= nr_seq_grupo_w;

c02 CURSOR FOR
SELECT	distinct
	a.dt_prevista
from	man_ordem_ativ_prev a
where	a.nr_seq_ordem_serv	= nr_seq_ordem_serv_p
and	a.nm_usuario_prev	= nm_usuario_prev_p
and	(a.dt_prevista IS NOT NULL AND a.dt_prevista::text <> '')
order by
	a.dt_prevista;


BEGIN

if (nr_seq_ordem_serv_p IS NOT NULL AND nr_seq_ordem_serv_p::text <> '') then
	update	man_ordem_ativ_prev
	set	dt_prevista		= dt_previsao_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_ordem_serv	= nr_seq_ordem_serv_p
	and	nm_usuario_prev		= nm_usuario_prev_p
	and	coalesce(dt_prevista::text, '') = '';

	if (dt_previsao_p IS NOT NULL AND dt_previsao_p::text <> '') then
		begin
			CALL man_gerar_previsao_dia(dt_previsao_p, nm_usuario_prev_p);
		end;
	end if;

	/*open c02;
	loop
	fetch c02 into
		dt_previsao_w;
	exit when c02%notfound;
		begin
		open C01;
		loop
		fetch C01 into
			nm_usuario_grupo_w;
		exit when C01%notfound;
			begin
			man_gerar_previsao_dia(dt_previsao_w, nm_usuario_grupo_w);
			end;
		end loop;
		close C01;
		end;
	end loop;
	close c02;*/
else
	select	max(nr_seq_grupo)
	into STRICT	nr_seq_grupo_w
	from	usuario_grupo_des
	where	nm_usuario_grupo	= nm_usuario_prev_p;

	if (coalesce(nr_seq_grupo_w::text, '') = '') then

		select	max(nr_seq_grupo)
		into STRICT	nr_seq_grupo_w
		from	usuario_grupo_des
		where	nm_usuario_grupo	= nm_usuario_p;

	end if;


	open C01;
	loop
	fetch C01 into
		nm_usuario_grupo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
			if (dt_previsao_p IS NOT NULL AND dt_previsao_p::text <> '') then
				begin
					CALL man_gerar_previsao_dia(dt_previsao_p, nm_usuario_grupo_w);
				end;
			end if;
		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
