SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_definir_impacto_requisitos ( nr_requisitos_p text, ie_impacto_p text, nm_usuario_p text, ds_mensagem_p INOUT bigint) AS $body$
DECLARE


qt_nao_aprovado_w	bigint;

c_requisitos CURSOR FOR
	SELECT	mosip.nr_sequencia
	from	man_ordem_serv_imp_pr mosip,
		reg_product_requirement rpr
	where	rpr.nr_sequencia	= mosip.nr_product_requirement
	and	rpr.ie_situacao		= 'A'
	and	ie_impacto_p		= 'I'
	and	mosip.nr_sequencia in (WITH RECURSIVE cte AS (

						SELECT	regexp_substr(nr_requisitos_p, '[^,]+', 1, level)
						
						(regexp_substr(nr_requisitos_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_requisitos_p, '[^,]+', 1, level))::text <> '')
					  UNION ALL

						SELECT	regexp_substr(nr_requisitos_p, '[^,]+', 1, level) JOIN cte c ON ()

) SELECT * FROM cte;
)
	
union

	select	mosip.nr_sequencia
	from	man_ordem_serv_imp_pr mosip,
		reg_product_requirement rpr
	where	rpr.nr_sequencia	= mosip.nr_product_requirement
	and	rpr.ie_situacao		= 'A'
	and	ie_impacto_p		<> 'I'
	and	(rpr.dt_aprovacao IS NOT NULL AND rpr.dt_aprovacao::text <> '')
	and	mosip.nr_sequencia in (WITH RECURSIVE cte AS (

						select	regexp_substr(nr_requisitos_p, '[^,]+', 1, level)
						
						(regexp_substr(nr_requisitos_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_requisitos_p, '[^,]+', 1, level))::text <> '')
					  UNION ALL

						select	regexp_substr(nr_requisitos_p, '[^,]+', 1, level) JOIN cte c ON ()

) SELECT * FROM cte;
);

BEGIN

	for	r_c01 in c_requisitos loop
		update	man_ordem_serv_imp_pr
		set	ie_impacto_requisito	= ie_impacto_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_sequencia	= r_c01.nr_sequencia;
	end loop;

	select	count(mosip.nr_sequencia)
	into STRICT	qt_nao_aprovado_w
	from	man_ordem_serv_imp_pr mosip,
		reg_product_requirement rpr
	where	rpr.nr_sequencia	= mosip.nr_product_requirement
	and	rpr.ie_situacao		= 'A'
	and	ie_impacto_p		<> 'I'
	and	coalesce(rpr.dt_aprovacao::text, '') = ''
	and	mosip.nr_sequencia in (WITH RECURSIVE cte AS (

						SELECT	regexp_substr(nr_requisitos_p, '[^,]+', 1, level)
						
						(regexp_substr(nr_requisitos_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_requisitos_p, '[^,]+', 1, level))::text <> '')
					  UNION ALL

						SELECT	regexp_substr(nr_requisitos_p, '[^,]+', 1, level) JOIN cte c ON ()

) SELECT * FROM cte;
);

	if (qt_nao_aprovado_w > 0) then
		begin
			ds_mensagem_p := 1073527; -- Existem PRS's não aprovados. Será necessário informar o tipo de impacto como 'Inclusão'
		end;
	end if;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
