CREATE OR REPLACE PROCEDURE man_definir_usuario_executor ( nr_seq_ordem_p bigint, nm_usuario_exec_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

			 
ie_exige_causa_dano_tipo_w		varchar(255);
nr_seq_estagio_apos_def_exec_w	varchar(255);
nr_seq_causa_dano_w		man_ordem_servico.nr_seq_causa_dano%type;
nr_seq_tipo_solucao_w		man_ordem_servico.nr_seq_tipo_solucao%type;


BEGIN 
 
ie_exige_causa_dano_tipo_w := obter_param_usuario(299, 453, obter_perfil_Ativo, nm_usuario_p, cd_estabelecimento_p, ie_exige_causa_dano_tipo_w);
nr_seq_estagio_apos_def_exec_w := obter_param_usuario(299, 309, obter_perfil_Ativo, nm_usuario_p, cd_estabelecimento_p, nr_seq_estagio_apos_def_exec_w);
 
select	coalesce(max(nr_seq_causa_dano),0), 
	coalesce(max(nr_seq_tipo_solucao),0) 
into STRICT	nr_seq_causa_dano_w, 
	nr_seq_tipo_solucao_w 
from	man_ordem_servico 
where	nr_sequencia = nr_seq_ordem_p;
 
if (ie_exige_causa_dano_tipo_w <> 'N') then 
	begin 
	 
	if (ie_exige_causa_dano_tipo_w = 'A') and 
		((nr_seq_causa_dano_w = 0) or (nr_seq_tipo_solucao_w = 0)) then 
		begin 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(269776);
		end;
	elsif (ie_exige_causa_dano_tipo_w = 'C') and (nr_seq_causa_dano_w = 0) then 
		begin 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(269778);
		end;
	elsif (ie_exige_causa_dano_tipo_w = 'T') and (nr_seq_tipo_solucao_w = 0)then 
		begin 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(269779);
		end;
	end if;
	 
	end;
end if;
 
update	man_ordem_servico 
set	nm_usuario_exec	= nm_usuario_exec_p, 
	dt_atualizacao	= clock_timestamp(), 
	nm_usuario	= nm_usuario_p 
where	nr_sequencia	= nr_seq_ordem_p;
 
if (coalesce(nr_seq_estagio_apos_def_exec_w,'0') <> '0') then 
	begin 
	update	man_ordem_servico 
	set	nr_seq_estagio	= nr_seq_estagio_apos_def_exec_w, 
		dt_atualizacao	= clock_timestamp(), 
		nm_usuario	= nm_usuario_p 
	where	nr_sequencia	= nr_seq_ordem_p;
	end;
end if;
 
CALL man_gerar_envio_comunicacao(nr_seq_ordem_p,'14','','',nm_usuario_p,'');
 
