CREATE OR REPLACE PROCEDURE man_definir_usuario_ini_exec ( nr_sequencia_p bigint, nm_usuario_exec_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_estagio_w	varchar(255);
ie_origem_os_w		man_ordem_servico.ie_origem_os%type;


BEGIN

if (coalesce(nr_sequencia_p,0) <> 0) then
	begin

	begin
	select	ie_origem_os
	into STRICT	ie_origem_os_w
	from	man_ordem_servico
	where	nr_sequencia = nr_sequencia_p;

	if (ie_origem_os_w = '4') then
		begin
		nr_seq_estagio_w := obter_param_usuario(299, 309, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, nr_seq_estagio_w);

		select	nr_sequencia
		into STRICT	nr_seq_estagio_w
		from	man_estagio_processo
		where	nr_sequencia = nr_seq_estagio_w;
		end;
	end if;
	exception
	when others then
		nr_seq_estagio_w := null;
	end;

	update	man_ordem_servico
	set	nm_usuario_exec	= nm_usuario_exec_p,
		nr_seq_estagio = coalesce(nr_seq_estagio_w,nr_seq_estagio),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;

	commit;
	end;
end if;

end;
