CREATE OR REPLACE PROCEDURE man_desvincular_os_etapa ( nr_seq_ordem_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_macro_w	varchar(1000);


BEGIN

update	man_ordem_servico
set	nr_seq_proj_cron_etapa	 = NULL,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_ordem_p;

update	man_ordem_serv_ativ
set	nr_seq_proj_cron_etapa	 = NULL
where	nr_seq_ordem_serv	= nr_seq_ordem_p;

delete
from	proj_ordem_servico
where	nr_seq_ordem = nr_seq_ordem_p;

ds_macro_w	:= 'NM_USUARIO=' || nm_usuario_p;
insert into man_ordem_serv_tecnico(
			nr_sequencia,
			nr_seq_ordem_serv,
			dt_atualizacao,
			nm_usuario,
			ds_relat_tecnico,
			dt_historico,
			ie_origem,
			dt_liberacao,
			nm_usuario_lib)
		values (	nextval('man_ordem_serv_tecnico_seq'),
			nr_seq_ordem_p,
			clock_timestamp(),
			nm_usuario_p,
--			'Ordem de servico desvinculada a etapa do cronograma pelo usuario: #@NM_USUARIO#@'
			obter_desc_exp_idioma(1028709, wheb_usuario_pck.get_nr_seq_idioma, ds_macro_w),
			clock_timestamp(),
			'I',
			clock_timestamp(),
			nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
