CREATE OR REPLACE PROCEDURE man_desvincular_os_item_rota ( nr_seq_item_p bigint, nr_seq_ordem_p bigint, nr_seq_estagio_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_rota_w	bigint;


BEGIN
select	nr_seq_rota_inspecao
into STRICT	nr_seq_rota_w
from	man_rota_inspecao_item
where	nr_sequencia = nr_seq_item_p;

update	man_rota_inspecao_item
set	nr_seq_ordem_serv  = NULL
where	nr_sequencia 	= nr_seq_item_p;

update	man_ordem_servico
set	dt_inicio_real	= coalesce(dt_inicio_real, dt_inicio_previsto),
	dt_fim_real	= clock_timestamp(),
	nr_seq_estagio	= nr_seq_estagio_p,
	ie_status_ordem	= 3,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario_encer	= nm_usuario_p
where	nr_sequencia	= nr_seq_ordem_p;

insert into man_ordem_serv_tecnico(
	nr_sequencia,
	nr_seq_ordem_serv,
	dt_atualizacao,
	nm_usuario,
	ds_relat_tecnico,
	dt_historico,
	ie_origem,
	dt_liberacao,
	nm_usuario_lib)
values (	nextval('man_ordem_serv_tecnico_seq'),
	nr_seq_ordem_p,
	clock_timestamp(),
	nm_usuario_p,
	'Ordem de serviço desvinculada do item da rota de inspeção nº ' || nr_seq_rota_w,
	clock_timestamp(),
	'I',
	clock_timestamp(),
	nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
