CREATE OR REPLACE PROCEDURE man_encerramento_programado (qt_dia_p bigint, nr_ordem_servico_p text) AS $body$
DECLARE


dt_encerramento_w timestamp;


BEGIN

select clock_timestamp() + qt_dia_p
into STRICT dt_encerramento_w
;

update MAN_ORDEM_SERVICO
set dt_conclusao_desejada = dt_encerramento_w
where nr_sequencia = nr_ordem_servico_p;
commit;
end;
