CREATE OR REPLACE PROCEDURE man_encerrar_os_des ( nr_seq_ordem_serv_p bigint, nr_seq_estagio_p bigint, nr_seq_tipo_solucao_p bigint, ie_grau_satisfacao_p text, nm_usuario_p text, ie_anexo_p text default null) AS $body$
DECLARE


qt_anexo_w		bigint;


BEGIN

	select	count(1)
	into STRICT	qt_anexo_w
	from	man_ordem_serv_arq
	where	nr_seq_ordem = nr_seq_ordem_serv_p;

	if (qt_anexo_w > 0) and (coalesce(ie_anexo_p,'S')	= 'S')then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(733577);
	end if;

	update	man_ordem_servico
	set		nr_seq_tipo_solucao = nr_seq_tipo_solucao_p,
			ie_grau_satisfacao = ie_grau_satisfacao_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
	where	nr_sequencia	= nr_seq_ordem_serv_p;

	CALL MAN_ENCERRAR_OS(nr_seq_ordem_serv_p,
						nr_seq_estagio_p,
						nm_usuario_p,
						null,
						'N');
commit;

end;
$body$
