SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_excluir_epic ( nr_epic_p bigint, nr_art_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


dt_inicial_w		desenv_art.dt_inicial%type;
dt_final_w		desenv_art.dt_final%type;
qt_epic_art_w		bigint;
qt_features_art_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_epic_art_w
from	desenv_epic_art a,
	desenv_art b
where 	a.nr_art = b.nr_sequencia
and	nr_epic = nr_epic_p
and	nr_art = nr_art_p
and	dt_inicial < clock_timestamp();

select	count(*)
into STRICT	qt_features_art_w
from	desenv_feature_release a,
	desenv_release b,
	desenv_feature c
where	b.nr_art = nr_art_p
and	a.nr_feature = c.nr_sequencia
and	c.nr_epic = nr_epic_p
and	(a.nr_release IS NOT NULL AND a.nr_release::text <> '');

if (qt_epic_art_w > 0) and (qt_features_art_w > 0) then
	ds_erro_p := wheb_mensagem_pck.get_texto(336112); --Não é possível excluir um Epic de um ART terminado ou em andamento.
	return;
else
	delete	from desenv_epic_art
	where	nr_epic = nr_epic_p
	and	coalesce(nr_art,0) = coalesce(nr_art_p,0);

	select	count(*)
	into STRICT	qt_epic_art_w
	from	desenv_epic_art
	where 	nr_epic = nr_epic_p;

	if (qt_epic_art_w = 0) then
		update	desenv_epic
		set	ie_excluido = 'S'
		where	nr_sequencia = nr_epic_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
