SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_excluir_feature ( nr_feature_p bigint, nr_release_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


dt_inicial_w		desenv_release.dt_inicial%type;
dt_final_w		desenv_release.dt_final%type;
qt_feature_release_w	bigint;
qt_stories_release_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_feature_release_w
from	desenv_feature_release a,
	desenv_release b
where 	a.nr_release = b.nr_sequencia
and	nr_feature = nr_feature_p
and	nr_release = nr_release_p
and	dt_inicial < clock_timestamp();

select	count(*)
into STRICT	qt_stories_release_w
from	desenv_story_sprint a,
	desenv_sprint b,
	desenv_story c
where	b.nr_release = nr_release_p
and	a.nr_story = c.nr_sequencia
and	c.nr_feature = nr_feature_p
and	(a.nr_sprint IS NOT NULL AND a.nr_sprint::text <> '');

if (qt_feature_release_w > 0) and (qt_stories_release_w > 0) then
	ds_erro_p := wheb_mensagem_pck.get_texto(336104); --Não é possível excluir uma Feature de um PI terminado ou em andamento.
	return;
else
	delete	from desenv_feature_release
	where	nr_feature = nr_feature_p
	and	coalesce(nr_release,0) = coalesce(nr_release_p,0);

	select	count(*)
	into STRICT	qt_feature_release_w
	from	desenv_feature_release
	where 	nr_feature = nr_feature_p;

	if (qt_feature_release_w = 0) then
		update	desenv_feature
		set	ie_excluido = 'S'
		where	nr_sequencia = nr_feature_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
