CREATE OR REPLACE PROCEDURE man_excluir_item_plano_insp (nr_seq_item_p bigint, ie_deleta_tipo_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_equipamento_w	bigint;
nr_seq_tipo_equip_w	bigint;
nr_seq_plano_w		bigint;
nr_seq_item_w		bigint;

c01 CURSOR FOR
SELECT	b.nr_sequencia
from	man_equipamento a,
	man_plano_insp_item b
where	a.nr_sequencia 		= b.nr_seq_equipamento
and	a.nr_seq_tipo_equip	= nr_seq_tipo_equip_w
and	b.nr_seq_plano_inspecao 	= nr_seq_plano_w;


BEGIN
if (nr_seq_item_p > 0) then
	begin
	select	nr_seq_equipamento,
		nr_seq_tipo_equip,
		nr_seq_plano_inspecao
	into STRICT	nr_seq_equipamento_w,
		nr_seq_tipo_equip_w,
		nr_seq_plano_w
	from	man_plano_insp_item
	where	nr_sequencia = nr_seq_item_p;

	if (nr_seq_tipo_equip_w > 0) then
		delete from man_plano_insp_item_local where nr_seq_item = nr_seq_item_p;
		delete from man_plano_insp_item_result where nr_seq_item = nr_seq_item_p;
		delete from man_plano_insp_item_acao where nr_seq_item = nr_seq_item_p;
		delete from man_plano_insp_item_ativ where nr_seq_item = nr_seq_item_p;
		delete from man_plano_insp_item where nr_sequencia = nr_seq_item_p;

		if (ie_deleta_tipo_p = 'S') then
			open C01;
			loop
			fetch C01 into
				nr_seq_item_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin
				delete from man_plano_insp_item_local where nr_seq_item = nr_seq_item_w;
				delete from man_plano_insp_item_result where nr_seq_item = nr_seq_item_w;
				delete from man_plano_insp_item_acao where nr_seq_item = nr_seq_item_w;
				delete from man_plano_insp_item_ativ where nr_seq_item = nr_seq_item_w;
				delete from man_plano_insp_item where nr_sequencia = nr_seq_item_w;
				end;
			end loop;
			close C01;
		end if;
	end if;

	delete from man_plano_insp_item where nr_sequencia = nr_seq_item_p;
	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
