SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_excluir_story ( nr_story_p bigint, nr_sprint_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


dt_inicial_w		desenv_sprint.dt_inicial%type;
dt_final_w		desenv_sprint.dt_final%type;
qt_story_sprint_w	bigint;
cd_status_w		desenv_story_sprint.cd_status%type;


BEGIN

select	count(*),
	coalesce(max(a.cd_status),'0')
into STRICT	qt_story_sprint_w,
	cd_status_w
from	desenv_story_sprint a,
	desenv_sprint b
where 	a.nr_sprint = b.nr_sequencia
and	nr_story = nr_story_p
and	nr_sprint = nr_sprint_p
and	dt_inicial < clock_timestamp();

if (qt_story_sprint_w > 0) and (cd_status_w <> '0') then
	ds_erro_p := wheb_mensagem_pck.get_texto(336092); --Não é possível excluir uma Story de um Sprint terminado ou em andamento.
	return;
else
	delete	from desenv_story_sprint
	where	nr_story = nr_story_p
	and	coalesce(nr_sprint,0) = coalesce(nr_sprint_p,0);

	select	count(*)
	into STRICT	qt_story_sprint_w
	from	desenv_story_sprint
	where 	nr_story = nr_story_p;

	if (qt_story_sprint_w = 0) then
		update	desenv_story
		set	ie_excluido = 'S'
		where	nr_sequencia = nr_story_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
