SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_finalizar_rota_inspecao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



ie_exige_os_item_fora_faixa_w	varchar(15);
ds_itens_w			varchar(4000)	:= '';
nr_seq_equipamento_w		bigint;
ds_equip_w			varchar(255)	:= '';

c01 CURSOR FOR
SELECT	substr(man_obter_dados_equipamento(a.nr_seq_equipamento,'D'),1,255) ds_equip,
	a.nr_seq_equipamento
from	man_rota_inspecao_item a
where	a.nr_seq_rota_inspecao = nr_sequencia_p
and	coalesce(a.nr_seq_ordem_serv::text, '') = ''
and	a.ie_status = 'S'
and	exists (	select	1
		from	man_inspecao_resultado c,
			man_rota_insp_item_result b
		where	a.nr_sequencia	= b.nr_seq_item
		and	c.nr_sequencia	= b.nr_seq_resultado
		and	((b.vl_medido > c.vl_maximo) or (b.vl_medido < c.vl_minimo)))
order by nr_seq_equipamento;


BEGIN
ie_exige_os_item_fora_faixa_w := obter_param_usuario(294, 12, obter_perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_exige_os_item_fora_faixa_w);

if (coalesce(ie_exige_os_item_fora_faixa_w,'N') = 'S') then
	open C01;
	loop
	fetch C01 into
		ds_equip_w,
		nr_seq_equipamento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_itens_w := substr(ds_itens_w || ds_equip_w || ',',1,4000);
		end;
	end loop;
	close C01;

	if (coalesce(ds_itens_w,'X') <> 'X') then
		/*(-20011,'Valor medido fora da faixa permitida, é necessário a abertura de uma OS:' || chr(13) || chr(10) ||
						ds_itens_w);*/
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263072,'DS_ITENS=' || ds_itens_w);
	end if;
end if;

update	man_rota_inspecao
set	dt_termino	= clock_timestamp(),
	nm_usuario 	= nm_usuario_p
where	nr_sequencia 	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
