SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_finalizar_suporte_remoto ( nr_seq_ordem_p bigint, nm_usuario_p text ) AS $body$
DECLARE

 
nm_executor_w			varchar(60);
dt_hora_w				varchar(10);
dt_dia_w				varchar(10);
nr_seq_tipo_w			varchar(10);
ie_cliente_w			varchar(1);
ie_mtvo_retorno_ws_w	varchar(15);
cd_pessoa_solicit_w		varchar(10);
cd_pessoa_usuario_w		varchar(10);


BEGIN 
 
	if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
 
		select	cd_pessoa_solicitante 
		into STRICT	cd_pessoa_solicit_w 
		from	man_ordem_servico 
		where	nr_sequencia = nr_seq_ordem_p;
 
		select	coalesce(max(cd_pessoa_fisica), '0') 
		into STRICT	cd_pessoa_usuario_w 
		from	usuario 
		where	nm_usuario = wheb_usuario_pck.get_nm_usuario;
 
		if (cd_pessoa_usuario_w = cd_pessoa_solicit_w) then 
			ie_cliente_w := 'S';
		else 
			ie_cliente_w := 'N';
		end if;
 
		if (obter_se_base_corp = 'S') or (obter_se_base_wheb = 'S') then	 
			nr_seq_tipo_w := '152'; --Suporte remoto - Finalizado 
			ie_mtvo_retorno_ws_w := '';
		else 
			nr_seq_tipo_w := obter_parametro_usuario_js(299, 200);
			ie_mtvo_retorno_ws_w := 'FSR';
		end if;
 
		insert into man_ordem_serv_tecnico(	nr_sequencia, 
				nr_seq_ordem_serv, 
				dt_atualizacao, 
				nm_usuario, 
				ds_relat_tecnico, 
				dt_historico, 
				ie_origem, 
				nr_seq_tipo, 
				dt_liberacao, 
				ie_mtvo_retorno_ws) 
		values (	nextval('man_ordem_serv_tecnico_seq'), 
				nr_seq_ordem_p, 
				clock_timestamp(), 
				nm_usuario_p, 
				--O #@IE_CLIENTE_COLABORADOR#@ #@NM_PESSOA_FISICA#@ identificou que não é mais preciso suporte remoto - Suporte remoto desativado. 
				wheb_mensagem_pck.get_texto(355325, 'IE_CLIENTE_COLABORADOR=' || CASE WHEN ie_cliente_w='S' THEN  obter_desc_expressao(285156, 'Cliente')  ELSE obter_desc_expressao(308143, 'Colaborador') END  || ';' || 
													'NM_PESSOA_FISICA=' || substr(obter_pessoa_fisica_usuario(wheb_usuario_pck.get_nm_usuario, 'N'), 1, 80)), 
				clock_timestamp(), 
				'I', 
				nr_seq_tipo_w, 
				clock_timestamp(), 
				ie_mtvo_retorno_ws_w);
 
		update	man_ordem_servico 
		set	ie_suporte_remoto = 'N', 
			dt_atualizacao = clock_timestamp(), 
			nm_usuario = nm_usuario_p 
		where	nr_sequencia = nr_seq_ordem_p;
 
	end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
