CREATE OR REPLACE PROCEDURE man_gerar_aprovacoes_documento ( nr_documento_p bigint, nm_usuario_p text) AS $body$
DECLARE


c01 CURSOR FOR
	SELECT	cd_pessoa_fisica
	from	pessoa_fisica
	where	cd_pessoa_fisica in (134,4933,11106);

v01	c01%rowtype;


BEGIN

open c01;
loop
fetch c01 into
	v01;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	insert into desenv_art_doc_aprovacao(
		cd_pessoa_fisica,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		nr_documento,
		nr_sequencia)
	values (	v01.cd_pessoa_fisica,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		nr_documento_p,
		nextval('desenv_art_doc_aprovacao_seq'));

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
