SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_aprov_faq ( nr_seq_faq_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tipo_faq_w	com_tipo_faq_aprov.nr_seq_tipo_faq%type;
cd_pessoa_aprovacao_w	com_tipo_faq_aprov.cd_pessoa_aprovacao%type;
qt_pessoa_aprovacao_w	com_faq_aprov.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	a.cd_pessoa_aprovacao
	into STRICT	cd_pessoa_aprovacao_w
	from	com_tipo_faq_aprov a
	where	a.nr_seq_tipo_faq = nr_seq_tipo_faq_w
	and	a.ie_situacao = 'A';



BEGIN
if (nr_seq_faq_p IS NOT NULL AND nr_seq_faq_p::text <> '') then
	select	max(a.nr_seq_tipo)
	into STRICT	nr_seq_tipo_faq_w
	from	com_faq a
	where	a.nr_sequencia = nr_seq_faq_p;

	open C01;
	loop
	fetch C01 into
		cd_pessoa_aprovacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	count(*)
		into STRICT	qt_pessoa_aprovacao_w
		from	com_faq_aprov
		where	nr_seq_faq 		= nr_seq_faq_p
		and	cd_pessoa_aprovacao 	= cd_pessoa_aprovacao_w;

		if (qt_pessoa_aprovacao_w = 0) then
			insert into com_faq_aprov(nr_sequencia,
				nr_seq_faq,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_pessoa_aprovacao,
				dt_aprovacao)
			values (nextval('com_faq_aprov_seq'),
				nr_seq_faq_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_aprovacao_w,
				null);
		end if;
		end;
	end loop;
	close C01;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
