CREATE OR REPLACE PROCEDURE man_gerar_assist_tec_equip ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_existe_w		bigint;
cd_cnpj_w		varchar(14);
cd_pessoa_fisica_w	varchar(10);


BEGIN

select	cd_pessoa_fisica,
	cd_cgc
into STRICT	cd_pessoa_fisica_w,
	cd_cnpj_w
from	man_equip_assistencia
where	nr_sequencia = nr_sequencia_p;

select	count(*)
into STRICT	qt_existe_w
from	assistencia_tecnica
where	((cd_cgc = cd_cnpj_w) or (cd_pessoa_fisica = cd_pessoa_fisica_w));

if (qt_existe_w = 0) then
	begin
	insert into assistencia_tecnica(
		nr_sequencia,
		cd_estabelecimento,
		nr_seq_tipo,
		dt_atualizacao,
		nm_usuario,
		cd_pessoa_fisica,
		cd_cgc,
		nm_contato,
		ds_telefone,
		ds_horario,
		ds_observacao,
		ds_conexao,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	SELECT	nextval('assistencia_tecnica_seq'),
		cd_estabelecimento_p,
		nr_seq_tipo,
		clock_timestamp(),
		nm_usuario_p,
		cd_pessoa_fisica,
		cd_cgc,
		nm_contato,
		ds_telefone,
		ds_horario,
		ds_observacao,
		'',
		clock_timestamp(),
		nm_usuario_p
	from	man_equip_assistencia
	where	nr_sequencia = nr_sequencia_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
