CREATE OR REPLACE PROCEDURE man_gerar_ativ_extra_agenda ( nr_seq_usu_controle_p bigint, nm_usuario_p text) AS $body$
DECLARE



dt_entrada_w		timestamp;
dt_agenda_w		timestamp;
qt_existe_w		bigint;
nm_usuario_w		varchar(15);
nr_seq_tipo_ativ_w		bigint;
nr_minuto_duracao_w	bigint;
ds_observacao_w		varchar(255);
ds_agenda_w		varchar(100);
ds_local_w		varchar(255);

c01 CURSOR FOR
SELECT	x.dt_agenda,
	y.nr_seq_tipo_ativ,
	x.nr_minuto_duracao,
	x.ds_observacao,
	x.ds_agenda,
	coalesce(x.ds_local, substr(obter_descricao_padrao('LOCAL_AGENDA_TASY','DS_LOCAL',x.nr_seq_local),1,255)) ds_local
from	tipo_agenda_tasy y,
	agenda_tasy x
where	x.nr_seq_tipo		= y.nr_sequencia
and	x.nm_usuario_agenda	= nm_usuario_w
and	trunc(x.dt_agenda) 		= dt_entrada_w
and	x.ie_status <> 'C'
and	(y.nr_seq_tipo_ativ IS NOT NULL AND y.nr_seq_tipo_ativ::text <> '');


BEGIN

select	trunc(dt_entrada),
	nm_usuario
into STRICT	dt_entrada_w,
	nm_usuario_w
from	usuario_controle
where	nr_sequencia	= nr_seq_usu_controle_p;

select	count(*)
into STRICT	qt_existe_w
from	agenda_tasy
where	nm_usuario 	= nm_usuario_w
and	trunc(dt_agenda)	= dt_entrada_w;

if (qt_existe_w > 0) then
	open c01;
	loop
	fetch c01 into
		dt_agenda_w,
		nr_seq_tipo_ativ_w,
		nr_minuto_duracao_w,
		ds_observacao_w,
		ds_agenda_w,
		ds_local_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	count(*)
		into STRICT	qt_existe_w
		from	usuario_atividade
		where	nr_seq_controle 	= nr_seq_usu_controle_p
		and	dt_atividade 	= dt_agenda_w
		and	nr_seq_tipo_ativ	= nr_seq_tipo_ativ_w;

		if (qt_existe_w = 0) then
			begin
			insert into usuario_atividade(
					nr_sequencia,
					nr_seq_controle,
					dt_atualizacao,
					nm_usuario,
					dt_atividade,
					nr_seq_tipo_ativ,
					qt_minuto,
					ds_observacao)
				values (	nextval('usuario_atividade_seq'),
					nr_seq_usu_controle_p,
					clock_timestamp(),
					nm_usuario_p,
					dt_agenda_w,
					nr_seq_tipo_ativ_w,
					nr_minuto_duracao_w,
					substr(	'Assunto: ' || ds_agenda_w || chr(13) || chr(10) ||
						'Local: ' || ds_local_w || chr(13) || chr(10) ||
						ds_observacao_w,1,255));
			end;
		end if;
		end;
	end loop;
	close c01;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
