CREATE OR REPLACE PROCEDURE man_gerar_ativ_prevista ( nr_seq_ordem_p bigint, nr_seq_funcao_p bigint, qt_min_prev_p bigint, nm_usuario_exec_p text, ie_commit_p text, nm_usuario_p text, nr_seq_ativ_exec_p bigint, ie_classif_os_p text default null, nr_seq_estagio_p bigint default null, nr_seq_origem_p bigint default null, nr_seq_localizacao_p bigint default null, nr_seq_gerencia_p bigint default null) AS $body$
DECLARE


qt_registro_w			bigint;
qt_prioridade_os_w			bigint := 10;
ie_desenv_w				varchar(10);
dt_prevista_w			timestamp;


BEGIN

if (ie_classif_os_p = 'E') then
	qt_prioridade_os_w := 10;
end if;

select	count(*)
into STRICT	qt_registro_w
from	man_ordem_ativ_prev
where	nr_seq_ordem_serv	= nr_seq_ordem_p
and	nr_seq_funcao	= nr_seq_funcao_p
and	nm_usuario_prev	= nm_usuario_exec_p;

if (qt_registro_w = 0) then

	select  max(ie_desenv)
	into STRICT    ie_desenv_w
	from    man_estagio_processo
	where   nr_sequencia    = nr_seq_estagio_p;

	dt_prevista_w	:= null;
	if (ie_desenv_w = 'S') and (nr_seq_origem_p > 0) and (nr_seq_localizacao_p	= 1272) and (nr_seq_gerencia_p in (3,11)) then
		dt_prevista_w	:= clock_timestamp();
	end if;

	insert into man_ordem_ativ_prev(
		nr_sequencia,
		nr_seq_ordem_serv,
		dt_atualizacao,
		nm_usuario,
		ds_atividade,
		dt_prevista,
		qt_min_prev,
		nm_usuario_prev,
		nr_seq_funcao,
		nr_seq_ativ_exec,
		ie_prioridade_desen,
		nm_usuario_nrec,
		dt_atualizacao_nrec)
	values (	nextval('man_ordem_ativ_prev_seq'),
		nr_seq_ordem_p,
		clock_timestamp(),
		nm_usuario_p,
		'',
		dt_prevista_w, /*Solicitação do Edilson*/
		least(qt_min_prev_p,420),
		nm_usuario_exec_p,
		nr_seq_funcao_p,
		nr_seq_ativ_exec_p,
		qt_prioridade_os_w,
		nm_usuario_p,
		clock_timestamp());
elsif (qt_registro_w > 0) then
	update	man_ordem_ativ_prev
	set	nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		qt_min_prev	= least(qt_min_prev_p,420)
	where	nr_seq_ordem_serv	= nr_seq_ordem_p
	and	nr_seq_funcao	= nr_seq_funcao_p
	and	nm_usuario_prev	= nm_usuario_exec_p;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
