SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_aval_ativ_prev_os ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tipo_avaliacao_w		bigint;

C01 CURSOR FOR
	SELECT	b.nr_seq_tipo_avaliacao
	from	des_ativ_exec b,
		man_ordem_ativ_prev a
	where	b.nr_sequencia = a.nr_seq_ativ_exec
	and	trunc(a.dt_prevista) >= trunc(clock_timestamp())
	and	coalesce(a.dt_real::text, '') = ''
	and	(b.nr_seq_tipo_avaliacao IS NOT NULL AND b.nr_seq_tipo_avaliacao::text <> '')
	and	a.nr_seq_ordem_serv = nr_sequencia_p
	and not exists (	SELECT	1
			from	man_ordem_serv_avaliacao x
			where	x.nr_seq_ordem_servico = a.nr_seq_ordem_serv
			and	x.nr_seq_tipo_avaliacao = b.nr_seq_tipo_avaliacao)
	group by b.nr_seq_tipo_avaliacao;


BEGIN

if (coalesce(nr_sequencia_p,0) <> 0) and (coalesce(nm_usuario_p,'X') <> 'X') then
	begin
	open C01;
	loop
	fetch C01 into
		nr_seq_tipo_avaliacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		insert into man_ordem_serv_avaliacao(
					nr_sequencia,
					nr_seq_ordem_servico,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					dt_avaliacao,
					nr_seq_tipo_avaliacao)
				values (	nextval('man_ordem_serv_avaliacao_seq'),
					nr_sequencia_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nr_seq_tipo_avaliacao_w);
	end loop;
	close C01;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
