CREATE OR REPLACE PROCEDURE man_gerar_aval_equip_os ( nr_seq_ordem_serv_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tipo_aval_w		man_equipamento_aval.nr_seq_tipo_avaliacao%type;
nr_seq_equipamento_w	man_equipamento.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_seq_tipo_avaliacao
	from	man_equipamento_aval
	where	nr_seq_equipamento = nr_seq_equipamento_w
	order by dt_atualizacao;


BEGIN

if (coalesce(nr_seq_ordem_serv_p,0) > 0) then
	begin

	begin
	select	nr_seq_equipamento
	into STRICT	nr_seq_equipamento_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_serv_p;
	exception
	when others then
		nr_seq_equipamento_w := 0;
	end;

	open C01;
	loop
	fetch C01 into
		nr_seq_tipo_aval_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		insert into man_ordem_serv_avaliacao(
				nr_sequencia,
				nr_seq_ordem_servico,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_avaliacao,
				nr_seq_tipo_avaliacao)
			values (nextval('man_ordem_serv_avaliacao_seq'),
				nr_seq_ordem_serv_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nr_seq_tipo_aval_w);
		end;
	end loop;
	close C01;

	commit;
	end;
end if;

end;
$body$
