SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_ccb_especialista ( nr_seq_impacto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ordem_serv_w						bigint;
nr_seq_grupo_w								bigint;
nr_seq_equipe_w								bigint;
qt_registro_w 							bigint;

/*
	- Buscar os PRSs em questão da MAN_ORDEM_SERV_IMP_PR (NR_PRODUCT_REQUIREMENT);
	- Para cada PRS verificar as funções vinculadas a ele REG_FUNCAO_PR;
	- Para cada função buscar o Grupo dessa função FUNCAO_GRUPO_DES / GRUPO_DESENVOLVIMENTO;
	- Para cada grupo diferente inserir na MAN_ORDEM_SERV_APROV_CCB um registro, com a equipe Especialista e para o grupo em questão;
*/
c01 CURSOR FOR
	SELECT	distinct f.nr_seq_grupo
	from		man_ordem_serv_impacto	a,
				man_ordem_serv_imp_pr	b,
				reg_product_requirement	c,
				reg_funcao_pr					d,
				funcao								e,
				funcao_grupo_des				f
	where	a.nr_sequencia					= b.nr_seq_impacto
	and		b.nr_product_requirement	= c.nr_sequencia
	and		c.ie_aprovacao_espec		= 'S'
	and		c.nr_sequencia					= d.nr_seq_product_req
	and		d.cd_funcao						= e.cd_funcao
	and		e.cd_funcao						= f.cd_funcao
	and		a.nr_sequencia					= nr_seq_impacto_p;


BEGIN

select	coalesce(max(nr_seq_ordem_serv),0)
into STRICT		nr_seq_ordem_serv_w
from		man_ordem_serv_impacto
where	nr_sequencia	= nr_seq_impacto_p;

open C01;
			loop
			fetch C01 into
				nr_seq_grupo_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				select	coalesce(max(nr_sequencia),0)
				into STRICT		nr_seq_equipe_w
				from		ccb_equipe
				where	ie_tipo_equipe = 'ES';

				select	count(1)
				into STRICT	qt_registro_w
				from	man_ordem_serv_aprov_ccb
				where	nr_seq_impacto	= nr_seq_impacto_p
				and		nr_seq_grupo	= nr_seq_grupo_w;

				if (nr_seq_equipe_w > 0) and (qt_registro_w = 0) then
						insert into man_ordem_serv_aprov_ccb(
							nr_sequencia,
							dt_atualizacao,
							dt_atualizacao_nrec,
							nm_usuario,
							nm_usuario_nrec,
							nr_seq_ordem_serv,
							nr_seq_equipe,
							nr_seq_impacto,
							ie_status,
							dt_aprovacao,
							nm_usuario_aprov,
							nr_seq_grupo)
						values (
							nextval('man_ordem_serv_aprov_ccb_seq'),
							clock_timestamp(),
							clock_timestamp(),
							nm_usuario_p,
							nm_usuario_p,
							nr_seq_ordem_serv_w,
							nr_seq_equipe_w,
							nr_seq_impacto_p,
							'P',
							null,
							null,
							nr_seq_grupo_w);
				end if;

			end loop;
			close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
