SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_exec_prev_local ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;
nr_seq_grupo_planej_w		bigint;
nr_seq_grupo_trabalho_w		bigint;
nm_usuario_exec_w		varchar(15);
nr_seq_localizacao_w		bigint;

C01 CURSOR FOR
SELECT	distinct
	nm_usuario_exec
from (	/*Usuários gerais da regra quando não existir grupo de trab e planej definidos na OS*/
	SELECT	nm_usuario_exec
	from	man_localizacao_usu_exec
	where	ie_situacao = 'A'
	and	ie_gerar_exec_os = 'S'
	and	coalesce(nr_seq_grupo_trabalho::text, '') = ''
	and	coalesce(nr_seq_grupo_planej::text, '') = ''
	and	nr_seq_grupo_trabalho_w	= 0
	and	nr_seq_grupo_planej_w 	= 0
	and	nr_seq_localizacao		= nr_seq_localizacao_w
	
union all

	/*Usuários gerais da regra quando não existir grupo de trab e planej definidos na OS*/

	select	nm_usuario_exec
	from	man_localizacao_usu_exec
	where	ie_situacao = 'A'
	and	ie_gerar_exec_os = 'S'
	and	coalesce(nr_seq_grupo_trabalho,coalesce(nr_seq_grupo_trabalho_w,0))	= coalesce(nr_seq_grupo_trabalho_w,0)
	and	coalesce(nr_seq_grupo_planej,coalesce(nr_seq_grupo_planej_w,0))	= coalesce(nr_seq_grupo_planej_w,0)
	and	nr_seq_localizacao 					= nr_seq_localizacao_w) alias8;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	man_localizacao_usu_exec
where	ie_situacao = 'A'
and	ie_gerar_exec_os = 'S';

if (qt_existe_w > 0) then
	begin
	select	coalesce(nr_grupo_planej,0),
		coalesce(nr_grupo_trabalho,0),
		nr_seq_localizacao
	into STRICT	nr_seq_grupo_planej_w,
		nr_seq_grupo_trabalho_w,
		nr_seq_localizacao_w
	from	man_ordem_servico
	where	nr_sequencia = nr_sequencia_p;

	open C01;
	loop
	fetch C01 into
		nm_usuario_exec_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(nm_usuario_exec_w,'X') <> 'X') then
			begin
			insert into man_ordem_servico_exec(
					nr_sequencia,
					nr_seq_ordem,
					dt_atualizacao,
					nm_usuario,
					nm_usuario_exec,
					qt_min_prev)
				values (	nextval('man_ordem_servico_exec_seq'),
					nr_sequencia_p,
					clock_timestamp(),
					nm_usuario_p,
					nm_usuario_exec_w,
					0);
			end;
		end if;
		end;
	end loop;
	close C01;
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
