SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_hist_ana ( nm_usuario_p text, ie_testou_prati_p text, ie_verif_aparencia_p text, ie_testou_perfor_p text, ie_rev_cod_fonte_p text, ie_rev_doc_obj_p text, ie_gerou_build_p text, ie_gerou_build_solic_p text, ie_gera_impacto_p text, ie_justificativa_p text, ie_libera_hist_p text, nr_seq_ordem_serv_p bigint, ie_comentario_p text default null) AS $body$
DECLARE


ds_hist_w				varchar(8000);
nr_seq_idioma_w			bigint;
ie_resp_teste_reprog_w	varchar(1);
dt_aprovacao_ccb_w	timestamp;
			

BEGIN

	select	max(mosi.dt_aprovacao)
	into STRICT	dt_aprovacao_ccb_w
	from	man_ordem_serv_impacto mosi
	where	mosi.nr_seq_ordem_serv = nr_seq_ordem_serv_p;
	
	nr_seq_idioma_w := 5;--Ingles(US)
	
	ds_hist_w := wheb_rtf_pck.get_cabecalho;
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_fonte(20);
					
	if (coalesce(dt_aprovacao_ccb_w::text, '') = '') then
		begin
			ds_hist_w :=	ds_hist_w || wheb_rtf_pck.get_quebra_linha();

			ds_hist_w :=	ds_hist_w || wheb_rtf_pck.get_negrito(True)
					|| obter_desc_expressao_idioma(935916, 'Testes nao relevantes para o design change control', nr_seq_idioma_w) || wheb_rtf_pck.get_negrito(False);

			ds_hist_w :=	ds_hist_w || wheb_rtf_pck.get_quebra_linha();
			ds_hist_w :=	ds_hist_w || wheb_rtf_pck.get_quebra_linha();
		end;
	else
		begin
			ds_hist_w :=	ds_hist_w || wheb_rtf_pck.get_quebra_linha();

			ds_hist_w :=	ds_hist_w || wheb_rtf_pck.get_negrito(True)
					|| obter_desc_expressao_idioma(935918, 'Testes relevantes para o design change control', nr_seq_idioma_w) || wheb_rtf_pck.get_negrito(False);

			ds_hist_w :=	ds_hist_w || wheb_rtf_pck.get_quebra_linha();
			ds_hist_w :=	ds_hist_w || wheb_rtf_pck.get_quebra_linha();
		end;
	end if;
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_negrito(True) || obter_desc_expressao_idioma(757368, 'Preencha as lacunas com [ X ].', nr_seq_idioma_w) || wheb_rtf_pck.get_negrito(False);
						
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha() || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_negrito(True) || obter_desc_expressao_idioma(302819, 'Checklist', nr_seq_idioma_w) || wheb_rtf_pck.get_negrito(False);
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha() || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || '[ ' || case ie_testou_prati_p when 'S' then 'X' end || ' ] ' || obter_desc_expressao_idioma(757438, 'Testou praticidade', nr_seq_idioma_w);

	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || '[ ' || case ie_verif_aparencia_p when 'S' then 'X' end || ' ] ' || obter_desc_expressao_idioma(757440, 'Verificou a aparencia', nr_seq_idioma_w);
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || '[ ' || case ie_testou_perfor_p when 'S' then 'X' end || ' ] ' || obter_desc_expressao_idioma(757442, 'Testou a performance', nr_seq_idioma_w);
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || '[ ' || case ie_rev_cod_fonte_p when 'S' then 'X' end || ' ] ' || obter_desc_expressao_idioma(757450, 'Revisou o codigo fonte', nr_seq_idioma_w);
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || '[ ' || case ie_rev_doc_obj_p when 'S' then 'X' end || ' ] ' || obter_desc_expressao_idioma(757452, 'Revisou Documentacao do objeto', nr_seq_idioma_w);
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || '[ ' || case ie_gerou_build_p when 'S' then 'X' end || ' ] ' || obter_desc_expressao_idioma(757454, 'GEROU BUILD DE OBJETO NO DICIONaRIO DE DADOS', nr_seq_idioma_w);
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || '[ ' || case ie_gerou_build_solic_p when 'S' then 'X' end || ' ] ' || obter_desc_expressao_idioma(763810, 'Documentou o release na gestao da versao', nr_seq_idioma_w);
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha() || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w ||  wheb_rtf_pck.get_negrito(True) || obter_desc_expressao_idioma(757468, 'Essa customizacao/correcao causa impacto em outra Funcao\Funcionalidade? Se ''Sim'' detalhar.', nr_seq_idioma_w)  || wheb_rtf_pck.get_negrito(False);
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || '[ ' || case ie_gera_impacto_p when 'S' then 'X' end || ' ] ' ||  obter_desc_expressao_idioma(327113, 'Sim', nr_seq_idioma_w) || '[ ' || case ie_gera_impacto_p when 'N' then 'X' end || ' ] ' ||  obter_desc_expressao_idioma(327114, 'Nao', nr_seq_idioma_w);

	if (ie_justificativa_p IS NOT NULL AND ie_justificativa_p::text <> '') then
		ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
	
		ds_hist_w := ds_hist_w || wheb_rtf_pck.get_texto_rtf(REPLACE(ie_justificativa_p, '\' , '\\'));
	end if;
	if (ie_comentario_p IS NOT NULL AND ie_comentario_p::text <> '') then
		ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
		ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
		ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
		ds_hist_w := ds_hist_w ||  wheb_rtf_pck.get_negrito(True) || obter_desc_expressao_idioma(291926, 'Inf. adicional', nr_seq_idioma_w)  || wheb_rtf_pck.get_negrito(False);	
		ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha();
		ds_hist_w := ds_hist_w || wheb_rtf_pck.get_texto_rtf(REPLACE(ie_comentario_p, '\' , '\\'));
	end if;
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_quebra_linha() || wheb_rtf_pck.get_quebra_linha() || wheb_rtf_pck.get_quebra_linha();
		
	ds_hist_w := ds_hist_w || obter_desc_expressao_idioma(297270, 'Realizado por', nr_seq_idioma_w) || ' ' || Obter_nome_usuario(nm_usuario_p) || wheb_rtf_pck.get_quebra_linha();
	
	ds_hist_w := ds_hist_w || OBTER_DESCRICAO_GERENCIA(OBTER_GERENCIA_USUARIO_WHEB(nm_usuario_p));
	
	ds_hist_w := ds_hist_w || wheb_rtf_pck.get_rodape;
	
	insert	into	man_ordem_serv_tecnico( nr_sequencia,
										nr_seq_ordem_serv,
										nr_seq_tipo,
										nm_usuario,
										nm_usuario_lib,
										dt_atualizacao,
										dt_historico,
										dt_liberacao,
										ds_relat_tecnico,
										ie_origem)
	values (nextval('man_ordem_serv_tecnico_seq'),
			nr_seq_ordem_serv_p,
			31, -- Teste do Analista
			nm_usuario_p,
			CASE WHEN ie_libera_hist_p='S' THEN  nm_usuario_p  ELSE null END ,
			clock_timestamp(),
			clock_timestamp(),
			CASE WHEN ie_libera_hist_p='S' THEN  clock_timestamp()  ELSE null END ,
			ds_hist_w,
			'I'	
			
	);
	
	select	max(ie_resp_teste_reprog)
	into STRICT	ie_resp_teste_reprog_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_serv_p;
	
	if (ie_resp_teste_reprog_w = 'A') then
		CALL man_atualiza_resp_teste_reprog(nr_seq_ordem_serv_p, '', nm_usuario_p);
	end if;	
	
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_gerar_hist_ana ( nm_usuario_p text, ie_testou_prati_p text, ie_verif_aparencia_p text, ie_testou_perfor_p text, ie_rev_cod_fonte_p text, ie_rev_doc_obj_p text, ie_gerou_build_p text, ie_gerou_build_solic_p text, ie_gera_impacto_p text, ie_justificativa_p text, ie_libera_hist_p text, nr_seq_ordem_serv_p bigint, ie_comentario_p text default null) FROM PUBLIC;

