CREATE OR REPLACE PROCEDURE man_gerar_hist_cronograma ( nm_usuario_p text, ds_justificativa_p text, nr_seq_tipo_p bigint, nr_seq_cliente_p bigint, nr_seq_projeto_p bigint, nr_seq_cron_p bigint, ie_opcao_p text, ds_descricao_p text default null) AS $body$
DECLARE


ds_historico_w 	varchar(4000);
ds_titulo_w 	varchar(4000);


BEGIN

if (ie_opcao_p = 'D') then
ds_historico_w 	:= SUBSTR(obter_texto_dic_objeto(714501, philips_param_pck.get_nr_seq_idioma, 'JUSTIFICATIVA=' || ds_justificativa_p),1,255);
ds_titulo_w 	:= SUBSTR(obter_texto_dic_objeto(714567, philips_param_pck.get_nr_seq_idioma, 'NR_SEQUENCIA=' || nr_seq_cron_p),1,255);

if (ds_descricao_p IS NOT NULL AND ds_descricao_p::text <> '') then
	ds_historico_w := ds_historico_w || ds_descricao_p;
end if;

insert into com_cliente_hist(
	nr_sequencia,
	nr_seq_tipo,
	nr_seq_cliente,
	nm_usuario,
	dt_historico,
	dt_atualizacao,
	ds_historico,
	nr_seq_projeto,
	ds_titulo,
	dt_liberacao)
values (nextval('com_cliente_hist_seq'),
	nr_seq_tipo_p,
	nr_seq_cliente_p,
	nm_usuario_p,
	clock_timestamp(),
	clock_timestamp(),
	ds_historico_w,
	nr_seq_projeto_p,
	ds_titulo_w,
	clock_timestamp());

end if;

if (ie_opcao_p = 'A') then
ds_historico_w 	:= SUBSTR(obter_texto_dic_objeto(1012312, philips_param_pck.get_nr_seq_idioma, 'NR_SEQUENCIA=' || nr_seq_cron_p),1,255);
ds_titulo_w 	:= SUBSTR(obter_texto_dic_objeto(1012312, philips_param_pck.get_nr_seq_idioma, 'NR_SEQUENCIA=' || nr_seq_cron_p),1,255);

insert into com_cliente_hist(
	nr_sequencia,
	nr_seq_tipo,
	nr_seq_cliente,
	nm_usuario,
	dt_historico,
	dt_atualizacao,
	ds_historico,
	nr_seq_projeto,
	ds_titulo,
	dt_liberacao)
values (nextval('com_cliente_hist_seq'),
	nr_seq_tipo_p,
	nr_seq_cliente_p,
	nm_usuario_p,
	clock_timestamp(),
	clock_timestamp(),
	ds_historico_w,
	nr_seq_projeto_p,
	ds_titulo_w,
	clock_timestamp());

end if;

commit;

end;
$body$
