SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_hist_equipamento ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_historico_w		text;
nr_seq_equipamento_w	bigint;


BEGIN

begin
select	a.ds_relat_tecnico,
	b.nr_seq_equipamento
into STRICT	ds_historico_w,
	nr_seq_equipamento_w
from	man_ordem_serv_tecnico a,
	man_ordem_servico b
where	b.nr_sequencia = a.nr_seq_ordem_serv
and	a.nr_sequencia = nr_sequencia_p;
exception
when others then
	nr_seq_equipamento_w := 0;
end;

if (coalesce(nr_seq_equipamento_w,0) > 0) and (coalesce(ds_historico_w,'0') <> '0') then
	begin
	insert into man_equipamento_hist(
				nr_sequencia,
				nr_seq_equipamento,
				dt_historico,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ds_historico,
				ie_origem)
			values (	nextval('man_equipamento_hist_seq'),
				nr_seq_equipamento_w,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_historico_w,
				'S');

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
