CREATE OR REPLACE PROCEDURE man_gerar_hist_os_vinculada ( nr_seq_hist_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ordem_serv_w	bigint;
nr_seq_os_vinc_w		bigint;
ds_historico_w		varchar(32000);
ie_origem_w		varchar(01);
nr_seq_tipo_hist_w		bigint;

c01 CURSOR FOR
SELECT	x.nr_sequencia
from	man_ordem_servico x
where	x.nr_sequencia in (	select	a.nr_sequencia
			from	man_ordem_servico a,
				man_ordem_serv_vinc b
			where	a.nr_sequencia = b.nr_seq_os_vinculada
			and	a.ie_status_ordem <> 3
			and	coalesce(a.dt_fim_real::text, '') = ''
			and	b.nr_seq_ordem_servico = nr_seq_ordem_serv_w)
and	x.ie_status_ordem <> 3;


BEGIN
if (coalesce(nr_seq_hist_p,0) > 0) then
	select	nr_seq_ordem_serv,
		ds_relat_tecnico,
		ie_origem,
		nr_seq_tipo
	into STRICT	nr_seq_ordem_serv_w,
		ds_historico_w,
		ie_origem_w,
		nr_seq_tipo_hist_w
	from	man_ordem_serv_tecnico
	where	nr_sequencia = nr_seq_hist_p;

	if (nr_seq_ordem_serv_w > 0) then
		open C01;
		loop
		fetch C01 into
			nr_seq_os_vinc_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			insert into man_ordem_serv_tecnico(	nr_sequencia,
							nr_seq_ordem_serv,
							dt_atualizacao,
							nm_usuario,
							ds_relat_tecnico,
							dt_historico,
							ie_origem,
							nr_seq_tipo,
							dt_liberacao,
							nm_usuario_lib)
						values (	nextval('man_ordem_serv_tecnico_seq'),
							nr_seq_os_vinc_w,
							clock_timestamp(),
							nm_usuario_p,
							ds_historico_w,
							clock_timestamp(),
							ie_origem_w,
							nr_seq_tipo_hist_w,
							clock_timestamp(),
							nm_usuario_p);
			end;
		end loop;
		close C01;
	commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
