SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_hist_prioridade (nr_seq_ordem_p bigint, ie_informacao_p bigint, ie_prioridade_anterior_p text, ie_prioridade_nova_p bigint, ds_motivo_p text, nm_usuario_p text, nr_seq_tipo_p bigint) AS $body$
DECLARE


ds_usuario_w				varchar(60);
ds_informacao_w				varchar(2000);
ds_prioridade_w				varchar(40);
ds_historico_w				man_ordem_serv_tecnico.ds_relat_tecnico%type;

nr_seq_localizacao_w		man_localizacao.nr_sequencia%type;
cd_cnpj_w					man_localizacao.cd_cnpj%type;
nr_seq_idioma_w				pessoa_juridica.nr_seq_idioma%type;

BEGIN

select	obter_nome_usuario(nm_usuario_p)
into STRICT	ds_usuario_w
;

select	CASE WHEN ie_informacao_p=1 THEN obter_desc_expressao(310388)/*'Desenvolvimento'*/
  ELSE obter_desc_expressao(310387)/*'Suporte'*/ END
into STRICT	ds_prioridade_w
;

/*ds_informacao_w	:= 'Em ' || to_char(sysdate,'dd/mm/yyyy hh24:mi:ss') || ',' || chr(13) || chr(10) ||
			'O usuário ' || ds_usuario_w || ' alterou a prioridade ' || ds_prioridade_w || ' de '||
			ie_prioridade_anterior_p || ' para ' || ie_prioridade_nova_p || '.' || chr(13) || chr(10) || chr(13) || chr(10);*/
ds_informacao_w := wheb_mensagem_pck.get_texto(305692,	'DS_USUARIO=' || ds_usuario_w || ';' ||
							'DS_PRIORIDADE=' || ds_prioridade_w || ';' ||
							'IE_PRIORIDADE_ANTERIOR=' || ie_prioridade_anterior_p || ';' ||
							'IE_PRIORIDADE_NOVA=' || ie_prioridade_nova_p);

ds_historico_w	:=  ds_informacao_w;

if (nr_seq_tipo_p in (1,2,6,129,130,132,143)) then -- Tipo de históricos que foram tradados no Servidor, que vão para cliente (externos)
	select	nr_seq_localizacao
	into STRICT	nr_seq_localizacao_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_p;

	if (nr_seq_localizacao_w IS NOT NULL AND nr_seq_localizacao_w::text <> '') then
		select	max(a.cd_cnpj)
		into STRICT	cd_cnpj_w
		from	man_localizacao	a
		where	a.nr_sequencia	= nr_seq_localizacao_w;

		if (cd_cnpj_w IS NOT NULL AND cd_cnpj_w::text <> '') then
			select	max(a.nr_seq_idioma)
			into STRICT	nr_seq_idioma_w
			from	pessoa_juridica	a
			where	a.cd_cgc	= cd_cnpj_w;

			if (nr_seq_idioma_w IS NOT NULL AND nr_seq_idioma_w::text <> '') then

				ds_historico_w := ds_historico_w || substr(obter_desc_expressao_idioma(293478, obter_desc_expressao(707599)/*'Motivo'*/
, nr_seq_idioma_w), 1, 255) || chr(13) || chr(10) || ds_motivo_p;

			end if;
		end if;
	end if;
else
	ds_historico_w := ds_historico_w || obter_desc_expressao(707599)/*'Motivo'*/
 || chr(13) || chr(10) || ds_motivo_p;
end if;

/* Atualiza a prioridade da Ordem de servico */

if (ie_informacao_p = 1) then
	update	man_ordem_servico
	set	ie_prioridade_desen	= ie_prioridade_nova_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_ordem_p
	and	coalesce(ie_prioridade_desen,0)	<> ie_prioridade_nova_p;
elsif (ie_informacao_p = 2) then
	update	man_ordem_servico
	set	ie_prioridade_sup	= ie_prioridade_nova_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_ordem_p
	and	coalesce(ie_prioridade_sup,0) <> ie_prioridade_nova_p;
end if;

insert into man_ordem_serv_tecnico(
	nr_sequencia,
	nr_seq_ordem_serv,
	dt_atualizacao,
	nm_usuario,
	dt_historico,
	ds_relat_tecnico,
	dt_liberacao,
	nm_usuario_lib,
	ie_origem,
	nr_seq_tipo)
values (nextval('man_ordem_serv_tecnico_seq'),
	nr_seq_ordem_p,
	clock_timestamp(),
	'Tasy',
	clock_timestamp(),
	ds_historico_w,
	clock_timestamp(),
	'Tasy',
	'I',
	nr_seq_tipo_p);
commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_gerar_hist_prioridade (nr_seq_ordem_p bigint, ie_informacao_p bigint, ie_prioridade_anterior_p text, ie_prioridade_nova_p bigint, ds_motivo_p text, nm_usuario_p text, nr_seq_tipo_p bigint) FROM PUBLIC;

