SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_ordem_corresp ( nr_sequencia_p bigint, nr_seq_corresp_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE


ds_relat_tecnico_w			varchar(255);
nr_seq_copia_w			bigint;


BEGIN

select	nextval('man_ordem_servico_seq')
into STRICT	nr_seq_copia_w
;

nr_seq_corresp_p		:= nr_seq_copia_w;
ds_relat_tecnico_w		:= 'Ordem de serviço aberta automaticamente após solução da OS: ' || nr_sequencia_p;

insert into man_ordem_servico(
	nr_sequencia,
	nr_seq_localizacao,
	nr_seq_equipamento,
	cd_pessoa_solicitante,
	dt_ordem_servico,
	ie_prioridade,
	ie_parado,
	ds_dano_breve,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ie_tipo_ordem,
	ie_status_ordem,
	nr_grupo_planej,
	nr_grupo_trabalho,
	nr_seq_estagio,
	ie_classificacao,
	ds_dano,
	cd_funcao,
	nr_seq_grupo_des,
	nr_seq_grupo_sup,
	ie_forma_receb,
	dt_inicio_desejado,
	dt_conclusao_desejada,
	nm_usuario_exec,
	ie_origem_os)
SELECT	nr_seq_copia_w,
	nr_seq_localizacao,
	nr_seq_equipamento,
	cd_pessoa_solicitante,
	dt_ordem_servico,
	ie_prioridade,
	ie_parado,
	ds_dano_breve,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	ie_tipo_ordem,
	'1',
	nr_grupo_planej,
	nr_grupo_trabalho,
	131,
	ie_classificacao,
	ds_dano,
	cd_funcao,
	7, /* Grupo Tecnologia */
	nr_seq_grupo_sup,
	ie_forma_receb,
	dt_inicio_desejado,
	dt_conclusao_desejada,
	nm_usuario_exec,
	ie_origem_os
from	man_ordem_servico
where	nr_sequencia	= nr_sequencia_p;

/* Gerar histórico identificando a abertura da OS correspondente */

insert	into man_ordem_serv_tecnico(
	nr_sequencia,
	nr_seq_ordem_serv,
	dt_atualizacao,
	nm_usuario,
	ds_relat_tecnico,
	cd_versao_tasy,
	dt_historico,
	ie_origem,
	nr_seq_tipo,
	dt_liberacao,
	nm_usuario_lib)
values (	nextval('man_ordem_serv_tecnico_seq'),
	nr_seq_copia_w,
	clock_timestamp(),
	'Tasy',
	ds_relat_tecnico_w,
	null,
	clock_timestamp(),
	'I',
	7,
	clock_timestamp(),
	'Tasy');

/* Define executor da OS na Tecnologia */

insert	into man_ordem_servico_exec(
	nr_sequencia,
	nr_seq_ordem,
	dt_atualizacao,
	nm_usuario,
	nm_usuario_exec,
	qt_min_prev,
	dt_ult_visao,
	nr_seq_funcao,
	dt_recebimento)
values (	nextval('man_ordem_servico_exec_seq'),
	nr_seq_copia_w,
	clock_timestamp(),
	nm_usuario_p,
	'Luis',
	null,
	null,
	null,
	clock_timestamp());

/* Atualizar a Ordem com o número da nova OS correspondente */

update	man_ordem_servico
set	nr_seq_corresp	= nr_seq_copia_w
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
