CREATE OR REPLACE PROCEDURE man_gerar_ordem_prev_nexec_dia ( nm_usuario_p text) AS $body$
DECLARE



nr_seq_ordem_serv_w		bigint;
nr_seq_grupo_sup_w		bigint;
nm_usuario_exec_w		varchar(15);

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_grupo_sup,
		a.nm_usuario_exec
	from	man_estagio_processo b,
		man_ordem_servico a
	where	b.nr_sequencia = a.nr_seq_estagio
	and	b.ie_suporte = 'S'
	and	b.ie_acao = 1
	and	b.nr_sequencia not in (141,511)
	and	a.ie_status_ordem <> '3'
	and	coalesce(a.dt_fim_real::text, '') = ''
	and not exists (	SELECT	1
			from	man_ordem_prev_nexec_dia x
			where	x.nr_seq_ordem_servico = a.nr_sequencia
			and	trunc(x.dt_atualizacao_nrec) = trunc(clock_timestamp()));


BEGIN

delete from man_ordem_prev_nexec_dia
where	coalesce(nr_seq_motivo::text, '') = ''
and	coalesce(ds_observacao::text, '') = ''
and	coalesce(ds_acao::text, '') = ''
and	trunc(dt_atualizacao_nrec) = trunc(clock_timestamp())
and	nr_seq_gerencia	= 5;

open C01;
loop
fetch C01 into
	nr_seq_ordem_serv_w,
	nr_seq_grupo_sup_w,
	nm_usuario_exec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into man_ordem_prev_nexec_dia(
				nr_sequencia,
				nr_seq_ordem_servico,
				nr_seq_gerencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_grupo_sup,
				nm_usuario_exec)
			values (	nextval('man_ordem_prev_nexec_dia_seq'),
				nr_seq_ordem_serv_w,
				5,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_grupo_sup_w,
				nm_usuario_exec_w);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
