SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_os_equip_dixtal ( nr_serie_equip_p text, nr_id_os_dixtal_p bigint, ds_dano_breve_p text, ds_dano_p text, ds_versao_soft_p text, nr_serie_sec_equip_p text, ds_versao_sec_soft_p text, nm_usuario_p text, nr_sequencia_p INOUT bigint, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_equipamento_w	bigint;
nr_grupo_planejamento_w	bigint;
nr_grupo_trabalho_w	bigint;
nr_seq_estagio_w		bigint;
nr_seq_localizacao_w	bigint;
cd_pessoa_solicitante_w	varchar(10);


BEGIN

if (coalesce(nr_serie_equip_p, '0') <> '0') then
	ds_erro_p := '';

	select	max(nr_sequencia)
	into STRICT	nr_seq_equipamento_w
	from	man_equipamento
	where	nr_serie = nr_serie_equip_p;

	if (coalesce(nr_seq_equipamento_w,0) > 0) then
		select	nr_seq_planej,
			nr_seq_trab,
			nr_seq_estagio_inicio_os,
			nr_seq_local
		into STRICT	nr_grupo_planejamento_w,
			nr_grupo_trabalho_w,
			nr_seq_estagio_w,
			nr_seq_localizacao_w
		from	man_equipamento
		where	nr_sequencia = nr_seq_equipamento_w;

		begin
		select	cd_pessoa_fisica
		into STRICT	cd_pessoa_solicitante_w
		from	usuario
		where	nm_usuario = nm_usuario_p;
		exception
		when others then
			cd_pessoa_solicitante_w := '';
		end;

		if (coalesce(cd_pessoa_solicitante_w,'X') = 'X') then
			ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(277219) || nm_usuario_p;
		end if;

		if (coalesce(ds_erro_p,'X') = 'X') then
			begin
			select	nextval('man_ordem_servico_seq')
			into STRICT	nr_sequencia_p
			;

			insert	into man_ordem_servico(
				nr_sequencia,
				nr_seq_localizacao,
				nr_seq_equipamento,
				cd_pessoa_solicitante,
				dt_ordem_servico,
				ie_prioridade,
				ie_parado,
				ds_dano_breve,
				dt_atualizacao,
				nm_usuario,
				dt_inicio_desejado,
				dt_conclusao_desejada,
				ds_dano,
				dt_inicio_previsto,
				dt_fim_previsto,
				dt_inicio_real,
				dt_fim_real,
				ie_tipo_ordem,
				ie_status_ordem,
				nr_grupo_planej,
				nr_grupo_trabalho,
				nr_seq_estagio,
				ie_classificacao,
				ie_forma_receb,
				nm_usuario_nrec,
				ie_origem_os,
				cd_externo_os)
			values(	nr_sequencia_p,				-- nr_sequencia,
				nr_seq_localizacao_w,			-- nr_seq_localizacao,
				nr_seq_equipamento_w,			-- nr_seq_equipamento,
				cd_pessoa_solicitante_w,			-- cd_pessoa_solicitante,
				clock_timestamp(),					-- dt_ordem_servico,
				'M',					-- ie_prioridade,
				'N',					-- ie_parado,
				substr(ds_dano_breve_p,1,80),		-- ds_dano_breve,
				clock_timestamp(),					-- dt_atualizacao,
				nm_usuario_p,				-- nm_usuario,
				clock_timestamp(),					-- dt_inicio_desejado,
				(clock_timestamp() + interval '15 days'),				-- dt_conclusao_desejada,
				ds_dano_p,				-- ds_dano,
				clock_timestamp(),					-- dt_inicio_previsto,
				(clock_timestamp() + interval '15 days'),				-- dt_fim_previsto,
				clock_timestamp(),					-- dt_inicio_real,
				null,					-- dt_fim_real,
				'1',					-- ie_tipo_ordem,
				'1',					-- ie_status_ordem,
				nr_grupo_planejamento_w,			-- nr_grupo_planej,
				nr_grupo_trabalho_w,			-- nr_grupo_trabalho,
				nr_seq_estagio_w,				-- nr_seq_estagio,
				'S',					-- ie_classificacao,
				'I',					-- ie_forma_receb,
				nm_usuario_p,				-- nm_usuario_nrec,
				4,					--ie_origem_os
				nr_id_os_dixtal_p);
			commit;
			end;
		end if;
	else
		ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(277220);
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
