SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_os_task ( nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_pessoa_solicitante_p text, ds_dano_breve_p text, ds_dano_p text, nm_usuario_exec_p text, nr_seq_ativ_exec_p bigint, dt_inicio_p timestamp, qt_horas_p bigint, nr_seq_etapa_p bigint, nr_product_requirement_p bigint) AS $body$
DECLARE


nr_seq_ordem_w                  man_ordem_servico.nr_sequencia%type;
nr_seq_localizacao_w            man_ordem_servico.nr_seq_localizacao%type;
nr_seq_equipamento_w            man_ordem_servico.nr_seq_equipamento%type;
cd_funcao_w			man_ordem_servico.cd_funcao%type;
ie_classificacao_w		man_ordem_servico.ie_classificacao%type         := 'S';
ie_forma_receb_w		man_ordem_servico.ie_forma_receb%type           := 'U';
dt_inicio_previsto_w		man_ordem_servico.dt_inicio_previsto%type       := clock_timestamp();
dt_fim_previsto_w		man_ordem_servico.dt_fim_previsto%type          := clock_timestamp() + interval '20 days';
nr_seq_estagio_w		man_ordem_servico.nr_seq_estagio%type           := 4;
ie_tipo_ordem_w			man_ordem_servico.ie_tipo_ordem%type            := '4';
nr_grupo_planej_w		man_ordem_servico.nr_grupo_planej%type          := 1;
nr_grupo_trabalho_w		man_ordem_servico.nr_grupo_trabalho%type        := 1;
ie_prioridade_w			man_ordem_servico.ie_prioridade%type            := 'M';
ie_status_ordem_w		man_ordem_servico.ie_status_ordem%type          := '1';
nr_seq_grupo_des_w		man_ordem_servico.nr_seq_grupo_des%type;
nr_seq_projeto_w		man_ordem_servico.nr_seq_projeto%type;
nr_seq_executor_w               proj_equipe_papel.nr_sequencia%type		:=null;


BEGIN

if (cd_estabelecimento_p = 22) then
        nr_seq_localizacao_w := 4976;
	nr_seq_equipamento_w := 5217;
elsif (cd_estabelecimento_p = 1) then
        nr_seq_localizacao_w := 1272;
	nr_seq_equipamento_w := 1395;
else
        nr_seq_localizacao_w := 4604;
	nr_seq_equipamento_w := 4758;
end if;

begin
	select  coalesce(pce.cd_funcao, ppr.cd_funcao),
		ppr.nr_seq_grupo_des,
		ppr.nr_sequencia
	into STRICT    cd_funcao_w,
		nr_seq_grupo_des_w,
		nr_seq_projeto_w
	from    proj_cron_etapa pce,
		proj_cronograma pcr,
		proj_projeto ppr
	where   pce.nr_seq_cronograma = pcr.nr_sequencia
	and     pcr.nr_seq_proj = ppr.nr_sequencia
	and     pce.nr_sequencia = nr_seq_etapa_p  LIMIT 1;
exception
        when no_data_found then
		cd_funcao_w := null;
		nr_seq_grupo_des_w := null;
		nr_seq_projeto_w := null;
	when too_many_rows then
		cd_funcao_w := null;
		nr_seq_grupo_des_w := null;
		nr_seq_projeto_w := null;
end;

nr_seq_ordem_w := man_gerar_os_proj_etapa(nr_seq_ordem_w, cd_pessoa_solicitante_p, nr_seq_localizacao_w, nr_seq_equipamento_w, ds_dano_breve_p, ds_dano_p, cd_funcao_w, ie_classificacao_w, ie_forma_receb_w, dt_inicio_previsto_w, dt_fim_previsto_w, nr_seq_estagio_w, ie_tipo_ordem_w, nr_grupo_planej_w, nr_grupo_trabalho_w, ie_prioridade_w, ie_status_ordem_w, nm_usuario_p, nr_seq_etapa_p, nr_seq_grupo_des_w, nr_seq_projeto_w, nr_seq_ativ_exec_p, qt_horas_p, dt_inicio_p, cd_estabelecimento_p, nr_seq_executor_w);


if (nr_seq_ordem_w > 0) then
	begin
	if (nm_usuario_exec_p IS NOT NULL AND nm_usuario_exec_p::text <> '') then
		begin
		insert into man_ordem_servico_exec(
			nr_sequencia,
			nr_seq_ordem,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nm_usuario_exec)
		SELECT	nextval('man_ordem_servico_exec_seq'),
			nr_seq_ordem_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			x.nm_usuario
		from	usuario x
		where	x.nm_usuario = nm_usuario_exec_p
		and	not exists (
			SELECT	1
			from	man_ordem_servico_exec y
			where	x.nm_usuario = y.nm_usuario_exec
			and	x.nr_seq_ordem = nr_seq_ordem_w);
		end;
	end if;
	
        if (nr_product_requirement_p > 0) then
                CALL proj_do_impact_analysis(nr_seq_ordem_w, nr_product_requirement_p, nm_usuario_p);
        end if;

        delete from w_proj_cron_etapa_os wos where wos.nr_sequencia = nr_sequencia_p;
        commit;

        end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_gerar_os_task ( nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_pessoa_solicitante_p text, ds_dano_breve_p text, ds_dano_p text, nm_usuario_exec_p text, nr_seq_ativ_exec_p bigint, dt_inicio_p timestamp, qt_horas_p bigint, nr_seq_etapa_p bigint, nr_product_requirement_p bigint) FROM PUBLIC;

