CREATE OR REPLACE PROCEDURE man_gerar_pessoa_local_comunic ( nr_seq_localizacao_p bigint, nr_seq_tipo_comunicado_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_pessoa_w			bigint;
qt_registro_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	man_pessoa_localizacao
	where	nr_seq_localizacao = nr_seq_localizacao_p
	and	coalesce(ie_comunicado,'N')   = 'S';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_pessoa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	begin
	select 	coalesce(count(*),0)
	into STRICT	qt_registro_w
	from	man_pessoa_localiz_comunic
	where	nr_seq_pessoa 		= nr_seq_pessoa_w
	and	nr_seq_tipo_comunicado 	= nr_seq_tipo_comunicado_p;
	exception
	when others then
		qt_registro_w := 0;
	end;

	if (qt_registro_w = 0) then
		begin
		insert into man_pessoa_localiz_comunic(
					nr_sequencia,
					nr_seq_pessoa,
					nr_seq_tipo_comunicado,
					dt_atualizacao,
					dt_atualizacao_nrec,
					nm_usuario,
					nm_usuario_nrec)
				values (	nextval('man_pessoa_localiz_comunic_seq'),
					nr_seq_pessoa_w,
					nr_seq_tipo_comunicado_p,
					clock_timestamp(),
					clock_timestamp(),
					nm_usuario_p,
					nm_usuario_p);
		end;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
