CREATE OR REPLACE PROCEDURE man_gerar_planej_gerencia ( nr_seq_gerencia_p bigint, dt_referencia_p text, ie_tipo_gerencia_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


dt_referencia_w			timestamp;
dt_final_w			timestamp;
nr_seq_grupo_w			bigint;
qt_registros_w			bigint;
nr_seq_grupo_sup_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	grupo_desenvolvimento
	where	nr_seq_gerencia	= nr_seq_gerencia_p
	and	ie_tipo_gerencia_p	= 'D';

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	grupo_suporte
	where	nr_seq_gerencia_sup	= nr_seq_gerencia_p
	and	ie_tipo_gerencia_p	= 'S';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_grupo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	dt_referencia_w	:= pkg_date_utils.start_of(to_date(dt_referencia_p,'dd/mm/yyyy'),'YYYY',0);
	dt_final_w	:= pkg_date_utils.add_month(dt_referencia_w,12,0);

	WHILE(dt_referencia_w < dt_final_w) LOOP
		BEGIN
		select	count(*)
		into STRICT	qt_registros_w
		from	man_planejamento_meta
		where	nr_seq_grupo_desenv	= nr_seq_grupo_w
		and	dt_mes_referencia	= dt_referencia_w;

		if (qt_registros_w = 0) then
			insert into man_planejamento_meta(nr_sequencia, cd_estabelecimento, dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
								nm_usuario_nrec, nr_seq_gerencia, nr_seq_grupo_desenv, dt_mes_referencia, qt_ordem_planejamento,
								qt_colaborador_grupo)
							values (nextval('man_planejamento_meta_seq'), cd_estabelecimento_p, clock_timestamp(), nm_usuario_p, clock_timestamp(),
								nm_usuario_p, nr_seq_gerencia_p, nr_seq_grupo_w, dt_referencia_w, null,
								null);
		end if;

		dt_referencia_w	:= pkg_date_utils.add_month(dt_referencia_w,1,0);
		END;
	END LOOP;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_seq_grupo_sup_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	dt_referencia_w	:= pkg_date_utils.start_of(to_date(dt_referencia_p,'dd/mm/yyyy'),'YYYY',0);
	dt_final_w	:= pkg_date_utils.add_month(dt_referencia_w,12,0);

	while(dt_referencia_w < dt_final_w) loop
		begin
		select	count(*)
		into STRICT	qt_registros_w
		from	man_planejamento_meta
		where	nr_seq_grupo_sup	= nr_seq_grupo_sup_w
		and	dt_mes_referencia	= dt_referencia_w;

		if (qt_registros_w = 0) then
			insert into man_planejamento_meta(nr_sequencia, cd_estabelecimento, dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
								nm_usuario_nrec, nr_seq_gerencia, nr_seq_grupo_desenv, dt_mes_referencia, nr_seq_grupo_sup)
							values (nextval('man_planejamento_meta_seq'), cd_estabelecimento_p, clock_timestamp(), nm_usuario_p, clock_timestamp(),
								nm_usuario_p, nr_seq_gerencia_p, nr_seq_grupo_w, dt_referencia_w, nr_seq_grupo_sup_w);
		end if;

		dt_referencia_w	:= pkg_date_utils.add_month(dt_referencia_w,1,0);
		end;
	end loop;
	end;
end loop;
close C02;

commit;

end;
$body$
