SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_requisicao_orc ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_local_estoque_w		smallint;
cd_pessoa_requisitante_w		varchar(10);
cd_material_w			integer;
qt_material_requisitada_w		double precision;
vl_material_w			double precision;
cd_estabelecimento_w		smallint	:= wheb_usuario_pck.get_cd_estabelecimento;
nr_seq_req_material_w		bigint;
nr_seq_req_item_material_w		bigint;
cd_operacao_estoque_w		smallint;
nr_seq_ordem_serv_w		bigint;
qt_material_w			integer;

c01 CURSOR FOR 	 
	SELECT	qt_item, 
		vl_item, 
		cd_material 
	from	man_ord_serv_orc_item 
	where	(cd_material IS NOT NULL AND cd_material::text <> '') 
	and	nr_seq_orc_ordem = nr_sequencia_p;


BEGIN 
 
select	nextval('requisicao_seq'), 
	substr(obter_valor_param_usuario(919,9,obter_perfil_ativo,nm_usuario_p,1),1,3), 
	substr(obter_valor_param_usuario(919,26,obter_perfil_ativo,nm_usuario_p,1),1,4), 
	substr(obter_pessoa_fisica_usuario(nm_usuario_p,'C'),1,255) cd_pessoa_orc 
into STRICT	nr_seq_req_material_w, 
	cd_operacao_estoque_w, 
	cd_local_estoque_w, 
	cd_pessoa_requisitante_w
;
 
if	((coalesce(cd_operacao_estoque_w::text, '') = '') or (coalesce(cd_local_estoque_w::text, '') = '')) then 
	/*(-20011,'Favor verificar os parâmetros 9 ou 26 da função Requisição de Materiais e Medicamentos');*/
 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263085);
end if;	
	 
select	nr_seq_ordem 
into STRICT	nr_seq_ordem_serv_w 
from	man_ordem_servico_orc 
where	nr_sequencia = nr_sequencia_p;
 
select	count(*) 
into STRICT	qt_material_w 
from	man_ord_serv_orc_item 
where	(cd_material IS NOT NULL AND cd_material::text <> '') 
and	nr_seq_orc_ordem = nr_sequencia_p;
 
if (qt_material_w > 0) then 
	begin 
	insert into	requisicao_material( 
		nr_requisicao, 
		nr_seq_ordem_serv, 
		cd_estabelecimento, 
		cd_local_estoque, 
		dt_solicitacao_requisicao, 
		dt_atualizacao, 
		nm_usuario, 
		cd_operacao_estoque, 
		cd_pessoa_requisitante, 
		ie_urgente) 
	values (	nr_seq_req_material_w, 
		nr_seq_ordem_serv_w, 
		cd_estabelecimento_w, 
		cd_local_estoque_w, 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_p, 
		cd_operacao_estoque_w, 
		cd_pessoa_requisitante_w, 
		'N');
 
	open c01;
	loop 
	fetch c01 into	qt_material_requisitada_w, 
			vl_material_w, 
			cd_material_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		select	coalesce(max(nr_sequencia),0) + 1 
		into STRICT	nr_seq_req_item_material_w 
		from	item_requisicao_material 
		where	nr_requisicao = nr_seq_req_material_w;
 
		insert into	item_requisicao_material( 
			nr_requisicao, 
			nr_sequencia, 
			cd_estabelecimento, 
			cd_material, 
			qt_material_requisitada, 
			vl_material, 
			dt_atualizacao, 
			nm_usuario) 
		values (	nr_seq_req_material_w, 
			nr_seq_req_item_material_w, 
			cd_estabelecimento_w, 
			cd_material_w, 
			qt_material_requisitada_w, 
			vl_material_w, 
			clock_timestamp(), 
			nm_usuario_p);
		end;
	end loop;
	close c01;
	end;
else 
	/*(-20011,'Não é possivel gerar requisição sem material');*/
 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263124);
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
