SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_resumo_dia ( dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_gerencia_w			bigint;
nr_seq_grupo_des_w		bigint;
nr_seq_grupo_sup_w		bigint;
nm_usuario_exec_w		varchar(15);
qt_ordem_w			bigint;
dt_referencia_w			timestamp;
dt_refer_inicial_w			timestamp;
dt_refer_final_w			timestamp;
nr_seq_ordem_w			bigint;
qt_os_do_dia_w			bigint;
qt_os_24_dia_w			bigint;
qt_os_48_dia_w			bigint;
qt_os_cliente_w			bigint;
qt_os_encerrada_w			bigint;
qt_os_erro_dia_w			bigint;
dt_desenv_w			timestamp;
dt_recebimento_w			timestamp;
qt_minuto_w			double precision;
dt_ordem_servico_w		timestamp;
dt_atualizacao_w			timestamp;
dt_inicio_w			timestamp;
dt_fim_w				timestamp;

c01 CURSOR FOR 
	SELECT a.nr_sequencia, 
		a.nr_seq_grupo_des, 
		a.nm_usuario_exec, 
		b.nr_seq_gerencia nr_seq_gerencia_des, 
		a.dt_ordem_servico, 
		trunc(obter_data_estagio_os(a.nr_sequencia,'D'), 'dd') dt_desenv_w, 
		trunc(a.dt_atualizacao, 'dd') dt_atualizacao 
	from  grupo_desenvolvimento b, 
		man_ordem_servico a 
	where	a.nr_seq_grupo_des = b.nr_sequencia 
	and	(b.nr_seq_gerencia IS NOT NULL AND b.nr_seq_gerencia::text <> '') 
	and exists (	SELECT	1 
			from	man_ordem_serv_estagio b, 
				man_estagio_processo c 
			where	a.nr_sequencia		= b.nr_seq_ordem 
			and	b.dt_atualizacao	between dt_inicio_w and dt_fim_w 
			and	b.nr_seq_estagio 	= c.nr_sequencia 
			and	c.ie_desenv		= 'S');
				
c02 CURSOR FOR 
SELECT a.nr_sequencia, 
	b.nr_sequencia nr_seq_grupo_sup, 
	a.nm_usuario_exec, 
	b.nr_seq_gerencia_sup, 
	a.dt_ordem_servico, 
	trunc(obter_data_estagio_os(a.nr_sequencia,'S'), 'dd') dt_desenv_w, 
	trunc(a.dt_atualizacao, 'dd') dt_atualizacao 
from	grupo_suporte b, 
	man_ordem_servico a 
where	a.nr_seq_grupo_sup = b.nr_sequencia 
and	(b.nr_seq_gerencia_sup IS NOT NULL AND b.nr_seq_gerencia_sup::text <> '') 
and	exists (	SELECT	1 
		from	man_ordem_serv_estagio b, 
			man_estagio_processo c 
		where	a.nr_sequencia	= b.nr_seq_ordem 
		and	b.dt_atualizacao	between dt_inicio_w and dt_fim_w 
		and	b.nr_seq_estagio 	= c.nr_sequencia 
		and	c.ie_suporte	= 'S' 
		and	c.ie_acao		= 1);


BEGIN 
 
dt_inicio_w 	:= trunc(clock_timestamp(),'month');
dt_fim_w 	:= fim_mes(clock_timestamp());
 
delete 	from man_resumo_os_dia 
where	dt_ordem_servico between dt_inicio_w and dt_fim_w;
 
dt_refer_inicial_w       := trunc(dt_referencia_p, 'dd');
dt_refer_final_w        := trunc(dt_referencia_p, 'dd') + 86399/86400;
dt_referencia_w         := trunc(dt_referencia_p, 'dd');
qt_os_do_dia_w         := 0;
qt_os_24_dia_w         := 0;
qt_os_48_dia_w         := 0;
qt_os_cliente_w         := 0;
qt_os_encerrada_w        := 0;
qt_os_erro_dia_w        := 0;
 
 
open C01;
loop 
fetch C01 into 
    nr_seq_ordem_w, 
    nr_seq_grupo_des_w, 
    nm_usuario_exec_w, 
    nr_seq_gerencia_w, 
	dt_ordem_servico_w, 
	dt_desenv_w, 
	dt_atualizacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
    begin 
 
	dt_recebimento_w	:= dt_desenv_w;
	 
	qt_ordem_w	:= 0;
	qt_os_do_dia_w	:= 0;
	qt_os_24_dia_w	:= 0;
	qt_os_48_dia_w	:= 0;
	 
	if (dt_recebimento_w IS NOT NULL AND dt_recebimento_w::text <> '') then 
		qt_ordem_w	:= 1;
 
 
		if (trunc(dt_desenv_w, 'dd') = trunc(dt_referencia_w, 'dd')) then 
			qt_os_do_dia_w	:= 1;
		elsif	((trunc(dt_desenv_w, 'dd') - trunc(dt_referencia_w, 'dd')) = 1) then 
			qt_os_24_dia_w	:= 1;
		else 
			qt_os_48_dia_w	:= 1;
		end if;
 
		select coalesce(max(1),0) 
		into STRICT  qt_os_erro_dia_w 
		from  MAN_DOC_ERRO b, 
			man_ordem_servico a 
		where  a.nr_sequencia     = nr_seq_ordem_w 
		and   a.nr_sequencia     = b.nr_seq_ordem 
		and   b.IE_ORIGEM_ERRO    = 'W';
		 
		select coalesce(max(1),0) 
		into STRICT  qt_os_cliente_w 
		from  usuario_grupo_des b, 
			man_ordem_servico_v a 
		where  a.nr_sequencia     = nr_seq_ordem_w 
		and   a.nm_usuario      = b.nm_usuario_grupo 
		and   a.nr_seq_grupo_des   = b.nr_seq_grupo 
		and   exists (SELECT 1 
			from  man_estagio_processo y, 
				Man_ordem_serv_estagio x 
			where  x.nr_seq_estagio= y.nr_sequencia 
			and   x.nr_seq_estagio    in (2,1511) 
			and   x.nr_seq_ordem = a.nr_sequencia);
		 
		select coalesce(max(1),0) 
		into STRICT  qt_os_encerrada_w 
		from  man_ordem_servico_v a 
		where  a.nr_sequencia     = nr_seq_ordem_w 
		and   a.nr_seq_estagio    = 9;
 
		 
	insert into man_resumo_os_dia(NR_SEQUENCIA      , 
        DT_ATUALIZACAO     , 
        NM_USUARIO       , 
        DT_ATUALIZACAO_NREC, 
        NM_USUARIO_NREC   , 
        NR_SEQ_GRUPO_DES   , 
        NM_USUARIO_BASE    , 
        DT_ORDEM_SERVICO    , 
        QT_ORDEM        , 
        NR_SEQ_GERENCIA, 
        nr_ordem_servico, 
        qt_ordem_dia, 
        qt_erro, 
        QT_ORDEM_24_HR, 
        QT_ORDEM_48_HR, 
        qt_ordem_cliente, 
        QT_ORDEM_ENCERRADA) 
    values (nextval('man_resumo_os_dia_seq'), 
        clock_timestamp(), 
        nm_usuario_p, 
        clock_timestamp(), 
        nm_usuario_p, 
        nr_seq_grupo_des_w, 
        coalesce(nm_usuario_exec_w, 'TASY'), 
        dt_desenv_w, 
        qt_ordem_w, 
        nr_seq_gerencia_w, 
        nr_seq_ordem_w, 
        qt_os_do_dia_w, 
        qt_os_erro_dia_w, 
        qt_os_24_dia_w, 
        qt_os_48_dia_w, 
        qt_os_cliente_w, 
        qt_os_encerrada_w);
		 
	end if;
		 
		 
    end;
end loop;
close C01;
 
/* Calcular movimentação do SUPORTE */
 
 
open C02;
loop 
fetch C02 into 
	nr_seq_ordem_w, 
	nr_seq_grupo_sup_w, 
	nm_usuario_exec_w, 
	nr_seq_gerencia_w, 
	dt_ordem_servico_w, 
	dt_desenv_w, 
	dt_atualizacao_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
 
	dt_recebimento_w	:= dt_desenv_w;
	 
	qt_ordem_w	:= 0;
	qt_os_do_dia_w	:= 0;
	qt_os_24_dia_w	:= 0;
	qt_os_48_dia_w	:= 0;
	 
	if (dt_recebimento_w IS NOT NULL AND dt_recebimento_w::text <> '') then 
		qt_ordem_w	:= 1;
 
		if (trunc(dt_desenv_w, 'dd') = trunc(dt_referencia_w, 'dd')) then 
			qt_os_do_dia_w	:= 1;
		elsif	((trunc(dt_desenv_w, 'dd') - trunc(dt_referencia_w, 'dd')) = 1) then 
			qt_os_24_dia_w	:= 1;
		else 
			qt_os_48_dia_w	:= 1;
		end if;
		 
		select	coalesce(max(1),0) 
		into STRICT	qt_os_encerrada_w 
		from	man_ordem_servico_v a 
		where	a.nr_sequencia     = nr_seq_ordem_w 
		and	a.nr_seq_estagio    = 9;
 
		insert into man_resumo_os_dia( 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_seq_grupo_des, 
			nr_seq_grupo_sup, 
			nm_usuario_base, 
			dt_ordem_servico, 
			qt_ordem, 
			nr_seq_gerencia, 
			nr_ordem_servico, 
			qt_ordem_dia, 
			qt_erro, 
			qt_ordem_24_hr, 
			qt_ordem_48_hr, 
			qt_ordem_cliente, 
			qt_ordem_encerrada) 
		values (	nextval('man_resumo_os_dia_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			null, 
			nr_seq_grupo_sup_w, 
			coalesce(nm_usuario_exec_w, 'TASY'), 
			dt_desenv_w, 
			qt_ordem_w, 
			nr_seq_gerencia_w, 
			nr_seq_ordem_w, 
			qt_os_do_dia_w, 
			0, 
			qt_os_24_dia_w, 
			qt_os_48_dia_w, 
			0, 
			qt_os_encerrada_w);
	end if;
		 
    end;
end loop;
close C02;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
