CREATE OR REPLACE PROCEDURE man_gerar_resumo_mes (dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
dt_inicio_w	timestamp;
dt_final_w	timestamp;
dt_atual_w	timestamp;


BEGIN 
 
dt_inicio_w	:= trunc(dt_referencia_p, 'month');
dt_final_w	:= fim_mes(dt_referencia_p);
 
dt_atual_w	:= dt_inicio_w;
 
while(dt_atual_w <= dt_final_w)loop 
	begin 
 
	if (dt_atual_w <= trunc(clock_timestamp(),'dd')) then 
		CALL Man_Gerar_Resumo_dia(dt_atual_w, nm_usuario_p);
	end if;
 
	dt_atual_w	:= dt_atual_w + 1;
 
	end;
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
