SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_score_os ( ie_area_gerencia_p text, nr_seq_gerencia_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text) AS $body$
DECLARE



c01 CURSOR FOR
SELECT 	a.ie_classificacao,
	coalesce(a.ie_prioridade_cliente,a.ie_prioridade) ie_prioridade,
	coalesce(a.nr_seq_severidade,a.nr_seq_severidade_wheb) nr_seq_severidade,
	substr(coalesce(man_obter_curva_abc(a.nr_seq_cliente,c.nr_seq_gerencia),'C'),1,5) ds_curva_abc,
	a.nr_seq_cliente,
	a.nr_sequencia,
	a.dt_ordem_servico,
	c.nm_usuario_lider,
	c.nr_seq_gerencia,
	a.qt_prioridade,
	a.nr_seq_estagio,
	a.nm_usuario_exec,
	a.nr_seq_grupo_des,
	a.nr_seq_localizacao,
	upper(a.DS_DANO_BREVE) ds_dano_breve,
	a.cd_funcao,
	f.cd_cnpj,
	a.NR_SEQ_TIPO_ORDEM
FROM man_localizacao f, man_estagio_processo b, man_ordem_servico a
LEFT OUTER JOIN grupo_desenvolvimento c ON (a.nr_seq_grupo_des = c.nr_sequencia)
LEFT OUTER JOIN gerencia_wheb d ON (c.nr_seq_gerencia = d.nr_sequencia)
WHERE a.nr_seq_estagio = b.nr_sequencia and f.nr_sequencia	= a.nr_seq_localizacao   and (a.nr_seq_grupo_des = nr_seq_grupo_p or coalesce(nr_seq_grupo_p::text, '') = '') and (c.nr_seq_gerencia = nr_seq_gerencia_p or coalesce(nr_seq_gerencia_p::text, '') = '') and (d.IE_AREA_GERENCIA = ie_area_gerencia_p or coalesce(ie_area_gerencia_p::text, '') = '') and (b.ie_desenv = 'S' or b.ie_tecnologia = 'S') and not exists (	select	1
		from	proj_projeto x
		where	x.nr_seq_ordem_serv  = a.nr_sequencia) and coalesce(a.nr_seq_proj_cron_etapa::text, '') = '';

qt_pontuacao_w		bigint;
qt_prioridade_w		bigint;
qt_classificacao_w	bigint;
qt_curva_w		bigint;
qt_dias_w		bigint;
qt_acordo_w		bigint;
qt_ponto_adicional_w	bigint;
ie_tipo_acordo_w	integer;
qt_dias_os_w		bigint;
nm_usuario_prev_w	varchar(15);
ie_funcao_usuario_w	varchar(15);
qt_priorizacao_w	bigint;
BEGIN



delete	FROM w_man_score_ordem
where	nm_usuario = nm_usuario_p;

for r_c01 in c01 loop
	begin

	qt_prioridade_w		:= 0;
	qt_pontuacao_w		:= 0;
	qt_classificacao_w	:= 0;
	qt_curva_w		:= 0;
	qt_dias_w		:= 0;
	qt_acordo_w		:= 0;

	if (r_c01.ie_prioridade = 'E') then
		qt_prioridade_w	:= 6;
	elsif (r_c01.ie_prioridade = 'U') then
		qt_prioridade_w := 4;
	elsif (r_c01.ie_prioridade = 'A') then
		qt_prioridade_w := 3;
	elsif (r_c01.ie_prioridade = 'M') then
		qt_prioridade_w := 2;
	else
		qt_prioridade_w := 1;
	end if;

	if	((position('S1' in r_c01.ds_dano_breve) > 0) or (position('S2' in r_c01.ds_dano_breve) > 0) or (position('S3' in r_c01.ds_dano_breve) > 0)) then
		qt_prioridade_w	:= qt_prioridade_w + 25;
	elsif	((position('S4' in r_c01.ds_dano_breve) > 0) or (position('S5' in r_c01.ds_dano_breve) > 0 )) then
		qt_prioridade_w	:= qt_prioridade_w + 5;
	end if;

	if (position('END2END' in upper(r_c01.ds_dano_breve)) > 0) then
		qt_prioridade_w	:= qt_prioridade_w + 25;
	end if;

	if (r_c01.cd_funcao	= -3001) then
		qt_prioridade_w	:= qt_prioridade_w + 15;
	end if;


	if (r_c01.NR_SEQ_TIPO_ORDEM = 294) then --HTML5 Performance
		qt_prioridade_w	:= qt_prioridade_w + 15;
	end if;


	select	count(1)  --OSs com histórico de priorização
	into STRICT	qt_priorizacao_w
	from	MAN_ORDEM_SERV_TECNICO a
	where	a.NR_SEQ_ORDEM_SERV	= r_c01.nr_sequencia
	and	a.NR_SEQ_TIPO		= 153;

	qt_prioridade_w	:= qt_prioridade_w + (qt_priorizacao_w * 10);



	if (r_c01.ie_classificacao = 'E') then -- erro
		if (r_c01.nr_seq_severidade = 1) then
			qt_classificacao_w :=  30;
		elsif (r_c01.nr_seq_severidade = 2) then
			qt_classificacao_w := 25;
		elsif (r_c01.nr_seq_severidade = 3) then
			qt_classificacao_w := 20;
		elsif (r_c01.nr_seq_severidade = 4) then
			qt_classificacao_w := 15;
		else
			qt_classificacao_w := 10;
		end if;
	elsif (r_c01.ie_classificacao = 'D') then -- dúvida
		qt_classificacao_w := 2;
	end if;

	if (r_c01.ds_curva_abc = 'S') then
		qt_curva_w := 15;
	elsif (r_c01.ds_curva_abc = 'A') then
		qt_curva_w := 10;
	elsif (r_c01.ds_curva_abc = 'B') then
		qt_curva_w := 7;
	elsif (r_c01.ds_curva_abc = 'C') then
		qt_curva_w := 4;
	end if;

	select	max(qt_ponto_adicional)
	into STRICT	qt_ponto_adicional_w
	from	gerencia_s_cliente a
	where	a.nr_seq_cliente	= r_c01.nr_seq_cliente
	and	(a.nr_seq_gerencia	= (r_c01.nr_seq_gerencia) or (coalesce(a.nr_seq_gerencia::text, '') = ''))
	and	clock_timestamp() between dt_inicial and dt_final + 86399/86400;


	if (coalesce(qt_ponto_adicional_w::text, '') = '') then
		select	max(qt_ponto_adicional)
		into STRICT	qt_ponto_adicional_w
		from	gerencia_s_cliente a
		where	a.CD_CNPJ	= r_c01.CD_CNPJ
		and	(a.nr_seq_gerencia	= (r_c01.nr_seq_gerencia) or (coalesce(a.nr_seq_gerencia::text, '') = ''))
		and	clock_timestamp() between dt_inicial and dt_final + 86399/86400;
	end if;


	if (qt_ponto_adicional_w IS NOT NULL AND qt_ponto_adicional_w::text <> '') then
		qt_curva_w	:=  qt_curva_w + qt_ponto_adicional_w;
	end if;





	select max(IE_TIPO_ACORDO)
	into STRICT	IE_TIPO_ACORDO_w
	from	desenv_acordo_os
	where	nr_seq_ordem_servico = r_c01.nr_sequencia;

	if (IE_TIPO_ACORDO_w	= 4) then --Obrig.Legal
		qt_acordo_w	:= 35;
	elsif (IE_TIPO_ACORDO_w	= 2) then --Virada
		qt_acordo_w	:= 30;
	elsif (IE_TIPO_ACORDO_w	= 3) then --Receita
		qt_acordo_w	:= 25;
	elsif (IE_TIPO_ACORDO_w	= 1) then --Priorização
		qt_acordo_w	:= 20;
	end if;

	if (r_c01.qt_prioridade IS NOT NULL AND r_c01.qt_prioridade::text <> '') then --Priridade informada na OS
		qt_acordo_w	:= qt_acordo_w	 + r_c01.qt_prioridade;
	end if;


	if (r_c01.nr_seq_localizacao	= 6881) then --Santa Helena
		qt_acordo_w	:= qt_acordo_w + 40;
	elsif (r_c01.nr_seq_localizacao	= 582) then --Santa Luiza
		qt_acordo_w	:= qt_acordo_w + 40;
	elsif (r_c01.nr_seq_localizacao	= 582) then --Santa helena (produção)
		qt_acordo_w	:= qt_acordo_w + 50;
	elsif (r_c01.nr_seq_localizacao	= 6808) then --Santa Luiza
		qt_acordo_w	:= qt_acordo_w + 40;
	elsif (r_c01.nr_seq_localizacao	= 16) then --Santa Luiza (produção)
		qt_acordo_w	:= qt_acordo_w + 50;
	elsif (r_c01.nr_seq_localizacao	= 6894) then --Anestblu
		qt_acordo_w	:= qt_acordo_w + 40;
	elsif (r_c01.nr_seq_localizacao	=6867) then --AC Camargo
		qt_acordo_w	:= qt_acordo_w + 85;
	elsif (r_c01.nr_seq_localizacao	= 5834) then --Marcio cunha
		qt_acordo_w	:= qt_acordo_w + 50;
	elsif (r_c01.nr_seq_localizacao	=2504) then --Hosp Alemão
		qt_acordo_w	:= qt_acordo_w + 80;
	elsif (r_c01.nr_seq_localizacao	=3144) then --CEON
		qt_acordo_w	:= qt_acordo_w + 20;
	elsif (r_c01.nr_seq_localizacao	=6851) then --IBCC
		qt_acordo_w	:= qt_acordo_w + 15;
	elsif (r_c01.nr_seq_localizacao	=6875) then --Mexico
		qt_acordo_w	:= qt_acordo_w + 15;
	end if;



	qt_dias_os_w	:= trunc(clock_timestamp() - r_c01.dt_ordem_servico);

	if (qt_dias_os_w	> 90) then
		qt_dias_w	:=  5;
	elsif (qt_dias_os_w	> 60) then
		qt_dias_w	:= 4;
	elsif (qt_dias_os_w	> 30) then
		qt_dias_w	:= 2;
	end if;

	qt_pontuacao_w	:= qt_prioridade_w + qt_classificacao_w + qt_curva_w + qt_dias_w + qt_acordo_w;

	nm_usuario_prev_w	:= obter_nome_usuario_exec2(r_c01.nr_sequencia, r_c01.nr_seq_estagio, r_c01.nm_usuario_exec);


	select	max(ie_funcao_usuario)
	into STRICT	ie_funcao_usuario_w
	from	USUARIO_GRUPO_DES
	where	NR_SEQ_GRUPO = r_c01.nr_seq_grupo_des
	and	NM_USUARIO_GRUPO = nm_usuario_prev_w;

	if (coalesce(ie_funcao_usuario_w::text, '') = '') then
		select	max(ie_funcao_usuario)
		into STRICT	ie_funcao_usuario_w
		from	USUARIO_GRUPO_DES
		where	NM_USUARIO_GRUPO = nm_usuario_prev_w;
	end if;


	insert into w_man_score_ordem(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_ordem_servico,
		dt_ordem_servico,
		ie_curva_abc,
		qt_pontuacao,
		nm_usuario_analista,
		ie_classificacao,
		nr_seq_severidade,
		ie_prioridade,
		qt_prioridade,
		qt_classificacao,
		qt_curva,
		qt_dias,
		qt_acordo,
		nm_usuario_prev,
		ie_funcao_usuario)
	values (	nextval('w_man_score_ordem_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		r_c01.nr_sequencia,
		r_c01.dt_ordem_servico,
		r_c01.ds_curva_abc,
		qt_pontuacao_w,
		null,
		r_c01.ie_classificacao,
		r_c01.nr_seq_severidade,
		r_c01.ie_prioridade,
		qt_prioridade_w,
		qt_classificacao_w,
		qt_curva_w,
		qt_dias_w,
		qt_acordo_w,
		nm_usuario_prev_w,
		ie_funcao_usuario_w);


	end;
end loop;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_gerar_score_os ( ie_area_gerencia_p text, nr_seq_gerencia_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text) FROM PUBLIC;

