CREATE OR REPLACE PROCEDURE man_gerar_solicitacao_equip ( nr_seq_prot_solic_p bigint, nr_seq_ordem_serv_p bigint, nr_nova_solic_compra_p INOUT bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
/* Variáveis do cursor C01 */
 
cd_material_w			integer;
qt_material_solicitada_w		double precision;
vl_material_w			double precision;
cd_unidade_medida_compra_w	varchar(30);

/* Sequências */
 
nr_seq_solic_compra_w 		bigint;
nr_seq_solic_compra_item_w		bigint;

/* Liberação */
 
dt_liberacao_w			timestamp;
nm_usuario_lib_w			varchar(15);

/* Executor */
 
qt_existe_exec			bigint;
cd_pessoa_solicitante_w		varchar(10);

/* Centro de custo */
 
nr_seq_localizacao_w		bigint;
qt_existe_centro_custo_w		bigint;
cd_centro_custo_w			integer;

/* Data entrega */
 
qt_dias_entrega_w			varchar(255);
dt_entrega_solicitada_w		timestamp;

qt_existe_w			bigint;

C01 CURSOR FOR 
	SELECT	qt_material, 
		cd_material 
	from	prot_solic_compra_item 
	where	(cd_material IS NOT NULL AND cd_material::text <> '') 
	and	nr_seq_protocolo = nr_seq_prot_solic_p;


BEGIN 
 
/* Procura centro de custo da localização */
 
select	coalesce(nr_seq_localizacao,0) 
into STRICT	nr_seq_localizacao_w 
from	man_ordem_servico 
where	nr_sequencia = nr_seq_ordem_serv_p;
 
select	count(cd_centro_custo) 
into STRICT	qt_existe_centro_custo_w 
from	man_localizacao 
where	nr_sequencia = nr_seq_localizacao_w;
 
if (qt_existe_centro_custo_w <> 0) then 
	select	cd_centro_custo 
	into STRICT	cd_centro_custo_w 
	from	man_localizacao 
	where	nr_sequencia = nr_seq_localizacao_w;
end if;
 
/* Procura executor como solicitante */
 
select	count(nm_usuario_exec) 
into STRICT	qt_existe_exec 
from	man_ordem_servico_exec 
where	nr_seq_ordem = nr_seq_ordem_serv_p;
 
if (qt_existe_exec <> 0) then 
	select	substr(obter_pessoa_fisica_usuario(min(nm_usuario_exec),'C'),1,10) 
	into STRICT	cd_pessoa_solicitante_w 
	from	man_ordem_servico_exec 
	where	nr_seq_ordem = nr_seq_ordem_serv_p;
end if;
 
/* Pega a data de entrega conforme parâmetro [ 8 ] */
 
select	coalesce(substr(obter_valor_param_usuario(913,8,obter_perfil_ativo,nm_usuario_p,cd_estabelecimento_p),1,255),'0') 
into STRICT	qt_dias_entrega_w
;
 
dt_entrega_solicitada_w := clock_timestamp() + qt_dias_entrega_w;
 
 
select	nextval('solic_compra_seq') 
into STRICT	nr_seq_solic_compra_w
;
 
nr_nova_solic_compra_p := nr_seq_solic_compra_w;
 
insert into solic_compra( 
		nr_solic_compra, 
		nr_seq_ordem_serv, 
		cd_estabelecimento, 
		cd_local_estoque, 
		dt_solicitacao_compra, 
		dt_atualizacao, 
		nm_usuario, 
		cd_conta_contabil, 
		cd_pessoa_solicitante, 
		ie_urgente, 
		ie_situacao, 
		ie_aviso_aprov_oc, 
		ie_aviso_chegada, 
		cd_centro_custo, 
		nm_usuario_nrec, 
		dt_atualizacao_nrec) 
SELECT		nr_seq_solic_compra_w, 
		nr_seq_ordem_serv_p, 
		cd_estabelecimento, 
		cd_local_estoque, 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_p, 
		cd_conta_contabil, 
		coalesce(cd_pessoa_solicitante_w,cd_pessoa_solicitante), 
		ie_urgente, 
		ie_situacao, 
		'N', 
		ie_aviso_chegada, 
		coalesce(cd_centro_custo_w,cd_centro_custo), 
		nm_usuario_p, 
		clock_timestamp() 
from	prot_solic_compra 
where	nr_sequencia = nr_seq_prot_solic_p;
 
open C01;
loop 
fetch C01 into 
	qt_material_solicitada_w, 
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMC'),1,255) cd_unidade_medida_compra 
	into STRICT	cd_unidade_medida_compra_w 
	from	material 
	where	cd_material = cd_material_w;
 
	select	coalesce(max(nr_item_solic_compra),0) + 1 
	into STRICT	nr_seq_solic_compra_item_w 
	from	solic_compra_item 
	where	nr_solic_compra = nr_seq_solic_compra_w;
 
	insert into	solic_compra_item( 
				nr_solic_compra, 
				nr_item_solic_compra, 
				cd_material, 
				qt_material, 
				cd_unidade_medida_compra, 
				dt_atualizacao, 
				nm_usuario, 
				dt_solic_item, 
				ie_situacao, 
				ie_geracao) 
	values (			nr_seq_solic_compra_w, 
				nr_seq_solic_compra_item_w, 
				cd_material_w, 
				qt_material_solicitada_w, 
				cd_unidade_medida_compra_w, 
				clock_timestamp(), 
				nm_usuario_p, 
				dt_entrega_solicitada_w, 
				'A', 
				'S');
 
 
 
	insert	into solic_compra_item_entrega( 
					dt_atualizacao, 
					dt_entrega_solicitada, 
					nm_usuario, 
					nr_item_solic_compra, 
					nr_item_solic_compra_entr, 
					nr_solic_compra, 
					qt_entrega_solicitada, 
					ds_observacao) 
		values (			clock_timestamp(), 
					dt_entrega_solicitada_w, 
					nm_usuario_p, 
					nr_seq_solic_compra_item_w, 
					1, 
					nr_seq_solic_compra_w, 
					qt_material_solicitada_w, 
					WHEB_MENSAGEM_PCK.get_texto(311327)); --Gerado pelo sistema 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
