CREATE OR REPLACE PROCEDURE man_gerar_usuario_exec_prior ( nr_seq_grupo_trab_p bigint, nr_seq_ordem_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

 
 
qt_usuario_exec_w	 		bigint;
qt_cd_cnpj_usuario_pj_w	 	bigint;
nr_seq_usuario_exec_w		bigint;
nr_seq_usuario_pj_w		bigint;
usuario_exec_w 		 	varchar(15);
cd_cnpj_usuario_pj_w	 	varchar(14);

C01 CURSOR FOR 
	SELECT	nm_usuario_param 
	from	man_grupo_trab_usuario a 
	where	(nr_seq_apres IS NOT NULL AND nr_seq_apres::text <> '') 
	and	nr_seq_grupo_trab = nr_seq_grupo_trab_p 
	and	ie_opcao_p = 'P' 
	
union
 
	SELECT	nm_usuario_param 
	from	man_grupo_trab_usuario a 
	where	coalesce(nr_seq_apres::text, '') = '' 
	and	ie_opcao_p = 'O' 
	and	nr_seq_grupo_trab = nr_seq_grupo_trab_p 
	
union
 
	select	nm_usuario_param 
	from	man_grupo_trab_usuario a 
	where	ie_opcao_p = 'S' 
	and	nr_seq_grupo_trab = nr_seq_grupo_trab_p 
	
union
 
	select	a.nm_usuario_param 
	from	man_grupo_trab_usuario a 
	where	a.nr_seq_apres = (select max(x.nr_seq_apres) 
				 from	 man_grupo_trab_usuario x 
				 where	 x.nr_seq_grupo_trab = nr_seq_grupo_trab_p) 
	and	ie_opcao_p = 'D' 
	
union
 
	select	a.nm_usuario_param 
	from	man_grupo_trab_usuario a 
	where	a.nr_seq_apres = (select min(x.nr_seq_apres) 
				 from	 man_grupo_trab_usuario x 
				 where	 x.nr_seq_grupo_trab = nr_seq_grupo_trab_p) 
	and	ie_opcao_p = 'M';

C02 CURSOR FOR 
	SELECT	a.cd_cnpj 
	from	man_grupo_trab_pj a 
	where	(nr_seq_apres IS NOT NULL AND nr_seq_apres::text <> '') 
	and	nr_seq_grupo_trabalho = nr_seq_grupo_trab_p 
	and	ie_opcao_p = 'P' 
	
union
 
	SELECT	a.cd_cnpj 
	from	man_grupo_trab_pj a 
	where	coalesce(nr_seq_apres::text, '') = '' 
	and	ie_opcao_p = 'O' 
	and	nr_seq_grupo_trabalho = nr_seq_grupo_trab_p 
	
union
 
	select	a.cd_cnpj 
	from	man_grupo_trab_pj a 
	where	ie_opcao_p = 'S' 
	and	nr_seq_grupo_trabalho = nr_seq_grupo_trab_p 
	
union
 
	select	a.cd_cnpj 
	from	man_grupo_trab_pj a 
	where	a.nr_seq_apres = (select max(x.nr_seq_apres) 
				 from	 man_grupo_trab_pj x 
				 where	 x.nr_seq_grupo_trabalho = nr_seq_grupo_trab_p) 
	and	ie_opcao_p = 'D' 
	
union
 
	select	a.cd_cnpj 
	from	man_grupo_trab_pj a 
	where	a.nr_seq_apres = (select min(x.nr_seq_apres) 
				 from	 man_grupo_trab_pj x 
				 where	 x.nr_seq_grupo_trabalho = nr_seq_grupo_trab_p) 
	and	ie_opcao_p = 'M';


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	usuario_exec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	select	count(*) 
	into STRICT	qt_usuario_exec_w 
	from	man_ordem_servico_exec 
	where	nr_seq_ordem = nr_seq_ordem_p 
	and	nm_usuario_exec = usuario_exec_w;
 
	if (qt_usuario_exec_w = 0) then 
		select	nextval('man_ordem_servico_exec_seq') 
		into STRICT	nr_seq_usuario_exec_w 
		;
 
		insert	into man_ordem_servico_exec(nr_sequencia, 
						nr_seq_ordem, 
						dt_atualizacao, 
						nm_usuario, 
						nm_usuario_exec) 
					values (	nr_seq_usuario_exec_w, 
						nr_seq_ordem_p, 
						clock_timestamp(), 
						nm_usuario_p, 
						usuario_exec_w);
		begin 
		CALL man_gerar_envio_comunicacao(	nr_seq_ordem_p, 
						'6', 
						'', 
						'', 
						nm_usuario_p, 
						'');
		exception 
		when others then 
			null;
		end;
	end if;
 
	end;
end loop;
close C01;
 
if (coalesce(usuario_exec_w,'X') <> 'X') then 
	begin 
	CALL man_gerar_envio_comunicacao(	nr_seq_ordem_p, 
					'6', 
					'', 
					'', 
					nm_usuario_p, 
					'');
	exception 
	when others then 
		null;
	end;
end if;
 
open C02;
loop 
fetch C02 into	 
	cd_cnpj_usuario_pj_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
 
	select	count(*) 
	into STRICT	qt_cd_cnpj_usuario_pj_w 
	from	man_ordem_serv_exec_pj 
	where	nr_seq_ordem_servico = nr_seq_ordem_p 
	and	cd_cnpj = cd_cnpj_usuario_pj_w;
 
	if (qt_cd_cnpj_usuario_pj_w = 0) then 
		select	nextval('man_ordem_serv_exec_pj_seq') 
		into STRICT	nr_seq_usuario_pj_w 
		;
 
		insert	into man_ordem_serv_exec_pj(nr_sequencia, 
						nr_seq_ordem_servico, 
						dt_atualizacao, 
						nm_usuario, 
						cd_cnpj) 
					values (	nr_seq_usuario_pj_w, 
						nr_seq_ordem_p, 
						clock_timestamp(), 
						nm_usuario_p, 
						cd_cnpj_usuario_pj_w);
	end if;
 
	end;
end loop;
close C02;
 
commit;
 
end;
$body$
