SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_gerar_versao_prevista ( nr_seq_ordem_serv_p man_ordem_servico.nr_sequencia%type, ds_justificativa_p text, cd_versao_p text, nm_usuario_p man_ordem_servico.nm_usuario%type) AS $body$
DECLARE


	ds_tipo_w		man_tipo_hist.ds_tipo%type;
	ds_mensagem_w		varchar(4000);
	ds_justificativa_w	varchar(4000);
	ie_atualiza_os_w	varchar(1);
	new_line_w		varchar(2)	:= chr(13) || chr(10);

BEGIN
	select	ds_tipo
	into STRICT	ds_tipo_w
	from	man_tipo_hist
	where	nr_sequencia = 4;
	
	select	obter_desc_expressao_idioma(1057972, 'Versao prevista para entrega da OS alterada para', wheb_usuario_pck.get_nr_seq_idioma) || ' ' || cd_versao_p || new_line_w
		|| obter_desc_expressao_idioma(325773, 'Justificativa:', wheb_usuario_pck.get_nr_seq_idioma) || ' ' || ds_justificativa_p ds_justificativa
	into STRICT	ds_justificativa_w
	;

	if (ds_tipo_w IS NOT NULL AND ds_tipo_w::text <> '') then
		ds_mensagem_w	:= ds_tipo_w || chr(10) || chr(13) || chr(10) || chr(13);
	end if;
	ds_mensagem_w := ds_mensagem_w || ds_justificativa_w;

	CALL man_gravar_historico_ordem(
			nr_seq_ordem_serv_p 	=> nr_seq_ordem_serv_p,
			dt_liberacao_p 		=> clock_timestamp(),
			ds_relat_tecnico_p 	=> ds_mensagem_w,
			ie_origem_p 		=> 'I',
			nr_seq_tipo_p 		=> 4,
			nm_usuario_p 		=> nm_usuario_p);
			
	if (cd_versao_p IS NOT NULL AND cd_versao_p::text <> '' AND nr_seq_ordem_serv_p IS NOT NULL AND nr_seq_ordem_serv_p::text <> '') then
		update	man_ordem_servico
		set	cd_versao_prevista = cd_versao_p
		where	nr_sequencia = nr_seq_ordem_serv_p;
		
		insert into man_os_ctrl_versao(
			nr_sequencia,
			nr_seq_ordem_serv,
			dt_atualizacao,
			nm_usuario,
			ds_justificativa,
			cd_estabelecimento,
			cd_versao)
		values (	nextval('man_os_ctrl_versao_seq'),
			nr_seq_ordem_serv_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_justificativa_p,
			wheb_usuario_pck.get_cd_estabelecimento,
			cd_versao_p);
		commit;
	end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
