CREATE OR REPLACE PROCEDURE man_gravar_anexos_ordem_serv ( nr_seq_faq_p bigint, nr_ordem_servico_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_arquivo_w 	varchar(255);
nm_arquivo_w	varchar(255);
c01 CURSOR FOR
	SELECT	ds_arquivo
	from	com_faq_anexo
	where	nr_seq_faq = nr_seq_faq_p;


BEGIN
if (nr_seq_faq_p IS NOT NULL AND nr_seq_faq_p::text <> '') and (nr_ordem_servico_p IS NOT NULL AND nr_ordem_servico_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
		fetch C01 into
			ds_arquivo_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nm_arquivo_w := '\\whebs05\ordem_servico\OS'|| nr_ordem_servico_p || '_';
		nm_arquivo_w := nm_arquivo_w || to_char(clock_timestamp(), 'dd') || '_' || to_char(clock_timestamp(), 'MM') || '_' || ds_arquivo_w;

		CALL man_gravar_anexo_ordem(nr_ordem_servico_p,nm_arquivo_w,nm_usuario_p);

		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
