CREATE OR REPLACE PROCEDURE man_gravar_ordem_serv_classif ( nr_seq_ordem_p bigint, ie_classificacao_p text, ie_classif_antiga_p text, nr_seq_justif_p bigint, ds_observacao_p text, nm_usuario_p text, nr_seq_justif_phi_p bigint default null) AS $body$
