	nr_sequencia,
	nr_seq_ordem_servico,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ie_classificacao,
	nr_seq_justificativa,
	ds_observacao,
	nr_seq_justif_phi)
values (	nextval('man_ordem_serv_classif_seq'),
