CREATE OR REPLACE PROCEDURE man_gravar_status_sms_ordem ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
SELECT	id_sms,
	dt_envio,
	nr_sequencia
from	man_ordem_serv_envio
where	(id_sms IS NOT NULL AND id_sms::text <> '')
and	coalesce(ie_status_sms::text, '') = ''
and (nr_seq_ordem = nr_sequencia_p or nr_sequencia_p = 0);

nr_sequencia_w		bigint;
id_sms_w			bigint;
dt_envio_w		timestamp;
ie_status_sms_w		varchar(15);


BEGIN

open C01;
loop
fetch C01 into
	id_sms_w,
	dt_envio_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	substr(wheb_sms.obter_status_sms(id_sms_w, dt_envio_w, nm_usuario_p),1,15)
	into STRICT	ie_status_sms_w
	;

	update	man_ordem_serv_envio
	set	ie_status_sms	= ie_status_sms_w,
      ds_observacao	= WHEB_MENSAGEM_PCK.get_texto(457974, 'id_sms='||id_sms_w) || substr(obter_valor_dominio(2353, ie_status_sms_w),1,255)
		--ds_observacao	= 'ID SMS=' || id_sms_w || ' STATUS=' || substr(obter_valor_dominio(2353, ie_status_sms_w),1,255)
	where	nr_sequencia	= nr_sequencia_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
